css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("dev.twitter.com") {
  .Page.Page--basicStyles.Page--dev.Page--devDocumentation {
    background-image: none;
  }
  
  .Page.Page--canopy.is-narrow .Footer .Footer-wrap, .Page.Page--canopy.is-narrow .Prompt, .Page.Page--canopy.is-narrow .PageLayout .PageLayout-wrap {
    max-width: 100%;
  }
  
  aside.SidebarFirst {
    background-color: #23435e;
  }
  .Node blockquote, .Node code, .Node table {
    display:inline;
  }
  .Node--documentation .Node-content th {
    width: auto;
  }
}
