css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(talkfreelance.com) {
	/*
		Make fluid width of 90% and replace 
		backgound tile with nice borders
	*/
	div.page {
		width: 90% !important;
		min-width: 770px !important;
		/* self-imposed width restriction, we don't need to go too wide! */
		max-width: 1170px !important;
		background: #fff !important;
		border-left: 7px solid #A5BBC9 !important;
		border-right: 7px solid #A5BBC9 !important;
	}
	div.page>div,
	div.page>table {
		border-left: 1px solid #678;
		border-right: 1px solid #678;
	}

	/* Fix for (talkfreelance) header image */
	div.page img[src="images/style_img/header.gif"] {
		position: relative;
		left: -8px;
	}

	/* Fix for grassy banner on homepage */
	div.page img[src="images/style_img/front_header.jpg"] {
		display: block;
		margin: 0 auto;
	}
	body > a + div > div > table > tbody > tr > td > div:first-child > div:first-child {
		border: 1px solid #678 !important;
		background-color: #e5eff6 !important;
		padding: 3px;
	}
}
