css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix(http://www.last.fm/user), url-prefix(http://beta.last.fm/user) {

#secondaryNavigation {
  position: relative !important;
  display: inline !important;
  width: 100% !important;
  left: 0 !important;
  top: 0 !important;  
  }
#secondaryNavigation ul {
  border: none !important;
  padding-top: 4px !important;
  }
#secondaryNavigation li {
  display: inline  !important;

  }
#secondaryNavigation li a {
  font-size: 12px !important;
  display: inline !important;
  margin: 0 !important;
  padding: 2px 2px 2px 9px !important;
  background: none !important;
  border: 5px !important;
  border-left: 1px solid #ccc !important;
  color: #777777 !important;
  }
#secondaryNavigation li.first a {
  border-left: none !important;
  }
#secondaryNavigation li a:hover,
#secondaryNavigation li.current a {
  color: #232323 !important;
  background: none !important;
  }
#secondaryNavigation li.current a {
  font-weight: 700 !important;
  border-right-color: #fff !important;
  }



#player
{
  display: none !important;
}

#content
{
  position: relative;
  display: block;

  background: white !important;
  margin-left: 5px !important;

}

#content > div:first-child
{
  position: absolute;
  width: 550px;
  top: 0;
  left: 180px;/*168*/
}

.leftCol
{
  margin-top: 20px !important;

}



/*--------RIGHT COLUMN------------*/
.leftCol
{
  float: right !important;
  display: block;
  margin-right: 3px !important;
  width: 875px !important;
  margin-left: 0px !important;

  background: white !important;
}

#taste
{
  display: none !important;
}


/*--------LEFT COLUMN------------*/
.rightCol
{
  display: block;
  float: left !important;
  width: 150px !important;
  margin-left: 0px !important;
}

.leftColWrapper
{
  width: 855px;

  padding-left: 5px !important;
margin-left: 0px !important;
}

.usersSmall
{
  margin-left: 10px !important;
}
.usersSmall li
{
  float: none !important;
  display: block !important;
  width: 100% !important;
  height: 42px !important;
  margin: 0 !important;

}
.groupsSmall
{
  padding-left: 10px !important;
}
.minifeedSmall
{
  display: none !important;
}


.candyStriped
{
  width: 710px;
}

.chartbarCell
{
  width: 400px !important;
}

/*----------HEADER-------------------*/


/*----------PROFILE-------------------*/
.rightCol
{
  display: block !important;
  margin-top: 240px !important;
}
#userBadge .badgeAvatar
{
  position: absolute;
  top: 40px;
  left: 10px;
  margin: 0 !important;
}
#userBadge h1
{
  position: absolute;
  top: 10px;
  left: 10px;
}
#userBadge .badgeInfo
{
  position: absolute;
  top: 160px;
  left: 0px;
  font-size: 10px !important;

  width: 140px;
}
#userBadge .badgeHead
{
  float: left;
height: 0px !important;
margin-top: -20px !important;
}


.profileHead .userData img
{
  display: none;
}
.profileHead .vcard
{
  position: absolute;
  top: 180px;
  left: 10px;
  width: 140px;
  font-size: 10px !important;
}
.profileHead .vcard strong
{
  display: block;
}
.profileHead .vcard span
{
  display: block;
}
.profileHead .userType
{
  position: absolute;
  top: 10px;
  left: 150px;
}

/*----------Ads-------------------*/
.LastAd, #footer_ads
{
  display: none !important;
}
