css:
@-moz-document domain("livestrong.com") {

#track_data iframe,
.livestrong_gpt iframe,
[id^="google_ads_iframe_"],
.intake.cal_progress,
.burned.cal_progress,
.module.chart_gold_member_links,
#redesign-container,
.column_2 a[href="/upgrade/"],
.column_2 a[href="/myplate/use-dashboard/"] img,
.column_2 a[href="/upgrade/"] + div,
.iphone_ad,
.top_header.flc {
    display: none !important;
    height: 0 !important;
}
#track_data {
    min-height: 0;
}
#tertiary_nav {
    height: 40px;
}
#tertiary_nav li a {
    padding: 0 15px;
}
#main {
    padding-bottom: 0;
    margin-bottom: 10px;
}
.column_2 a[href="/myplate/use-dashboard/"]:before {
    content: 'Use New Dashboard';
    background: #FFCB32;
    display: inline-block;
    margin-bottom: 10px;
    padding: 5px 11px;
    color: #000;
    font-weight: bold;
    -webkit-border-radius: 5px;
       -moz-border-radius: 5px;
            border-radius: 5px;
    cursor: pointer;
}
 
}
