css:
@-moz-document domain("theaquila.net") {
#content {
    /*background: url(http://www.theaquila.net/images/blackevo4-sweet/background.jpg);
    background-size: 100%;
    background-repeat: no-repeat;*/
    background-color: #0A0A0A;
}

#header #logo .wrapper a:first-child img{
    content: url(http://i.imgur.com/ZnViGXV.png);
}

.forum_on .fa-comments:before {
  content: url(http://www.theaquila.net/images/blackevo4-sweet/on.gif);
}

.forum_off .fa-comments:before {
  content: url(http://www.theaquila.net/images/blackevo4-sweet/off.gif);
}


a:visited {
  text-decoration: none;
  color: #d31579;
}
a:link {
  color: #d31579;
  text-decoration: none;
}
a:hover, a:active {
	color: #d7b17f !important;
	text-decoration: underline !important;
}

.thead {
  background: url(http://www.theaquila.net/images/blackevo4-sweet/thead_bg.gif) top left repeat-x;
  height: 0px;
  /*line-height: 30px;
  margin: 0 8px 0 8px;*/
  padding: 6px;
}
#panel .lower {
  background: #222;
  border-top: 2px solid #111;
  padding: 5px;
  color: #efefef;
}
#search input.button {
  background: #d31579;
  color: #fff;
}

/*.post {
    background-color: #0a0a0a;
  background-image: url(/web/20140329201311/http://www.theaquila.net/images/blackevo4-sweet/trow_bg.gif);
  background-repeat: repeat-x;
  background-position: bottom;
}*/

.trow2, .trow1 {
  background-color: #0a0a0a;
  background-image: url(http://www.theaquila.net/images/blackevo4-sweet/trow_bg.gif);
  background-repeat: repeat-x;
  background-position: bottom;
}

.tcat {
  color: #3d3b35;
  font-size: 12px;
  background: #0d0d0d;
  border-top: none;
}

#logo{
    border-bottom: none;
}

#container {
  color: #a2a2a2;
  background-color: #121212;
}

.post_content {
    background-color: #0a0a0a;
}

.post .post_author, .post .post_controls {
    background: rgb(13, 13, 13);
}

.post .post_author div.author_avatar img {
  padding: 5px;
  border: none;
  background: transparent;
}

/*config page*/
.tab-links a {
    background: url(http://www.theaquila.net/images/blackevo4-sweet/thead_bg.gif) top left repeat-x;
    color: #d31579;
}
.tab-links a:hover {
    background: #FF0559;
    color: #dead00 !important;
}
.tab-content{
    background: transparent;
}

/*fix mybb bug*/
.ui-helper-hidden-accessible {
    display: none;
}}
