@namespace url(http://www.w3.org/1999/xhtml);

/* Defaults
	/*[[Skin_Main_Color]]*/ = #A24141
	/*[[Skin_Logo]]*/ = http://i48.tinypic.com/35ix9pu.png
*/

@-moz-document domain("salesforce.com") {
    
    body {
    background-color: /*[[Skin_Main_Color]]*/;
    }
            
    body.sfdcBody {
        background-color: /*[[Skin_Main_Color]]*/;
    }

    .zen .zen-headerBottom {
        background: linear-gradient(#E5F2F6, /*[[Skin_Main_Color]]*/) repeat scroll 0% 0% transparent;
