@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slaent.com") {

body {
        background: #141414 !important;
        color: #cecece !important;
    }
h1.forum__title{
    color: #FFFFFF !important;
}
/* TYPE// Modifiers for fonts in their entirety. */
a {     color: #0015FF !important; }
a:hover { color: #FFFFFF !important; }
 
h5, h6{ color: #cecece !important; }
 
.page-nav__icon:hover { background: #A3ABFF !important; }
 
 
 /*Twitter*/.navigation__icon:before
        { background: #0015ff !important; }
 
/*Avatar Strings*/ .post__avatar:before
        { background: #0015ff !important; }
/*Avatar Borders*/ .post__avatar-wrapper
        { background: #cecece !important; border: .36em solid #0015FF !important; }
/*Member Tags*/ .post__username, .post__thread
        { color: #A3ABFF !important; line-height: 1em !important; }
/*Member Tags, alt.*/ .navigation__user-username
        { font-weight: 600 !important; }
 
.post-form__icon            
        { color: #0015ff !important; }
.post-form__icon:hover
        { color: #FFFFFF !important; }
 
.post-form__button--submit
        { background: #0015FF !important; border-color: #0015FF !important; color: #FFFFFF !important; }
.post-form__button--submit:hover
        { background: #FFFFFF !important; border-color: #FFFFFF !important; color: #0015FF !important; }
 
