css:
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Nightly Fox
 * by NadirP
 * http://nadirp.deviantart.com/
 * v16.01.21
 * Firefox 43.0.4 
 * Windows 10 */

/* SETINGS */

:root {
    --main-caption-bg-color: /*[[cap_col]]*/;
    --main-page-bg-color: /*[[bg_col]]*/;
    --main-page-cell-bg-color: rgba(255, 255, 255, .05);
    --main-page-text-color: /*[[txt_col]]*/;
    --main-page-text-color-two: /*[[a_col]]*/;
    --main-page-text-invert-color-two: /*[[txt_col]]*/;
    --main-page-link-color: /*[[a_col]]*/;
    --main-nav-bg-color: /*[[tbg_col]]*/;
    --main-url-bg-color: /*[[tbg_col]]*/;
    --main-url-text-color: /*[[ttxt_col]]*/;
    --main-tab-active-bg-color: /*[[tbg_col]]*/;
    --main-tab-active-hover-bg-color: /*[[tabg_col]]*/;
    --main-tab-active-text-color: /*[[ttxt_col]]*/;
    --main-tab-inactive-bg-color: /*[[itbg_color]]*/;
    --main-tab-inactive-hover-bg-color: /*[[ithbg_color]]*/;
    --main-tab-inactive-text-color: /*[[ittxt_color]]*/;
    --main-border-radius: 0px;
    --main-theme-border-color: rgba(0, 0, 0, 0);
    --main-page-border-color: rgba(0, 0, 0, 0);
    /*--main-font-family: "Open Sans", sans-serif;
    --main-theme-font-size: 13px;*/
}
    
/* THEME */
    
/* TITLEBAR COLOR */

#tab-view-deck {
    background-color: var(--main-caption-bg-color) !important;
}
/* TABS BACKGROUND SETTINGS */

#main-window:not([privatebrowsingmode="true"])[sizemode="normal"] #TabsToolbar {
    margin-left: 0px !important;
    margin-right: 0px !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}
#main-window:not([privatebrowsingmode="true"])[sizemode="maximized"] #TabsToolbar {
    margin-right: 0px !important;
    margin-left: 0px !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}
#main-window[sizemode="normal"]:not([inFullscreen]) #TabsToolbar {
    background: transparent !important;
}
#main-window[sizemode="maximized"] #TabsToolbar {
    background: transparent !important;
    margin-top: 0px !important;
}
#TabsToolbar:not(:-moz-lwtheme)::after {
    display: none;
}
/* TABS */

#TabsToolbar .arrowscrollbox-scrollbox {
    padding: 0px !important;
}
#TabsToolbar .tabbrowser-tabs {
    margin-left: -1px !important;
    margin-right: -2px !important;
}
#TabsToolbar .tabbrowser-tab[selected] {
    font-weight: normal !important;
    border: 1px solid var(--main-theme-border-color) !important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    background: var(--main-tab-active-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    transition-property: background, color;
    transition-duration: 0ms;
}
#TabsToolbar .tabbrowser-tab:hover[selected] {
    font-weight: normal !important;
    border: 1px solid var(--main-theme-border-color) !important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    background: var(--main-tab-active-hover-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    transition-property: background, color;
    transition-duration: 0ms;
}
#TabsToolbar .tabbrowser-tab {
    text-align: left!important;
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    background: var(--main-tab-inactive-bg-color) !important;
    border: 1px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
    color: var(--main-tab-inactive-text-color) !important;
    margin: 0px -1px 0px 0px !important;
    padding: 0px 0px 1px 0px;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
    transition-property: background, color;
    transition-duration: 150ms;
}
#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
    font-weight: normal !important;
    border: 1px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    background: var(--main-tab-inactive-hover-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    transition-property: background, color;
    transition-duration: 150ms;
}
/* TAB ICON & TAB LOADING ICON */

tab .tab-icon,
.tab-icon-image,
.tab-throbber {
    filter: grayscale(1);
}
#TabsToolbar .tabbrowser-tab[selected] .tab-icon,
#TabsToolbar .tabbrowser-tab[selected] .tab-icon-image,
#TabsToolbar .tabbrowser-tab[selected] .tab-throbber {
    filter: grayscale(0);
}
/* NEW TAB */

#main-window .tabbrowser-tabs .tabs-newtab-button {
    list-style-image: none;
    width: 35px;
    margin: 0px;
    padding: 0px;
    background: url(data:image/png;base64,) center no-repeat var(--main-tab-inactive-bg-color) !important;
    border: 1px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
    border-radius: 0px !important;
}
#main-window .tabbrowser-tabs .tabs-newtab-button:hover {
    list-style-image: none;
    background: url(data:image/png;base64,) center no-repeat var(--main-tab-inactive-hover-bg-color) !important;
    border: 1px solid var(--main-theme-border-color)!important;
    border-bottom: 0px solid transparent!important;
}
/* DISABLE AUSTRALIS TAB */

#TabsToolbar .tab-background {
    background: none;
}
#TabsToolbar .tab-background-start::before,
#TabsToolbar .tab-background-end::before {
    background-image: none !important;
}
.tab-background-start[selected=true]:-moz-locale-dir(ltr)::after,
.tab-background-end[selected=true]:-moz-locale-dir(ltr)::after {
    background-image: none!important;
}
#TabsToolbar .tab-background-middle {
    background: transparent !important;
}
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}
/* NAVIGATION BAR/ADDRESS BAR/SEARCH BAR SETTING */

#TabsToolbar:not(:-moz-lwtheme)::after {
    display: none;
}
#navigator-toolbox:after {
    height: 0px !important;
}
/* NAV-BAR */

#nav-bar {
    background: var(--main-nav-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-theme-border-color) !important;
    border: 1px solid var(--main-theme-border-color) !important;
    margin-top: 0px !important;
    color: var(--main-url-text-color) !important;
}
#main-window[sizemode="maximized"] #nav-bar {
    border-radius: 0px !important;
}
#main-window[sizemode="normal"]:not([inFullscreen]) #nav-bar {
    border-radius: 0px !important;
}
/* BACK BUTTON */

#forward-button .toolbarbutton-icon {
    border-radius: 0 25px 25px 0!important;
}
/* URL BAR */

#urlbar {
    margin-left: 7px !important;
    background: var(--main-url-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-url-bg-color) !important;
    border-right: 1px solid var(--main-theme-border-color)!important;
    border-radius: 0px !important;
    color: var(--main-url-text-color)!important;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
}
/* IDENTITY */

#identity-box,
#identity-icon-labels {
    background: transparent !important;
}
/* .bad-content-blocked */

#notification-popup-box {
    -moz-appearance: none !important;
    background: transparent !important;
    background-image: none!important;
    box-shadow: none !important;
    border-image: none !important;
    border: 0px;
    list-style-image: none;
}
/* SEARCH BAR */

#searchbar .searchbar-textbox {
    margin-left: -7px !important;
    background: var(--main-url-bg-color) !important;
    box-shadow: none !important;
    border-color: var(--main-url-bg-color) !important;
    border-left: 1px solid var(--main-theme-border-color)!important;
    border-right: 1px solid var(--main-theme-border-color)!important;
    border-radius: 0px !important;
    color: var(--main-url-text-color)!important;
    font-family: var(--main-font-family) !important;
    font-size: var(--main-theme-font-size) !important;
}
/* BOOKMARKS  */

#PersonalToolbar {
    background: var(--main-nav-bg-color) !important;
    border: 1px solid var(--main-theme-border-color) !important;
    border-top: 0px !important;
}
#PersonalToolbar .toolbarbutton-text {
    color: var(--main-url-text-color) !important;
}
/* TABS CLOSE BUTTON */

.tabs-closebutton-box,
.tab-close-button {
    filter: grayscale(1) contrast(200%) invert(1);
}
.tabs-closebutton-box:hover,
.tab-close-button:hover {
    filter: grayscale(1) contrast(200%) invert(0);
}
/* MENU BUTTON */

#PanelUI-menu-button {
    border-left: 1px solid transparent !important;
}
#PanelUI-button {
    background: transparent !important;
    border-image: none !important;
    border: transparent !important;
}
/* HIDES PLUGINS NOTIFICATION ICON THAT MAY CHANGE ADDRESS BAR'S HEIGHT */

#plugins-notification-icon {
    display: none !important;
}
/* REMOVES VS SEPARATOR */

#browser-bottombox {
    border: none !important;
}
/* BORDERLESS*/

#browser-border-end,
#browser-border-start {
    background: transparent !important;
    border-left: 1px solid var(--main-page-border-color)!important;
}
#browser-bottombox {
    background: transparent !important;
}
/* BORDERLESS EFFECT FOR BORDERLESS VS */

#main-window {
    -moz-appearance: -moz-win-borderless-glass !important;
    -moz-appearance: -moz-win-glass !important;
}
/* TOOLTIPS */

#btTooltip,
#un-toolbar-tooltip,
#tooltip,
.tooltip,
#aHTMLTooltip,
#urlTooltip,
tooltip,
#aHTMLTooltip,
#urlTooltip,
#brief-tooltip,
#brief-tooltip-last-updated,
#brief-tooltip-no-unread,
#brief-tooltip-feed-name,
#brief-tooltip-unread-count,
#brief-tooltip-unread-desc,
#brief-tooltip-rows,
#showcaseTooltipLeftArrow,
#showcaseTooltipRightArrow,
#linkReporterTitleTooltip,
#linkReporterURLTooltip,
#linkReporterURLIconTooltip,
#btTooltipTextBox,
#abp-tooltip,
#un-toolbar-tooltip,
.tooltip-label tooltip * {
    -moz-appearance: none !important;
    -moz-window-shadow: none !important;
    padding: 2px 6px !important;
    background: var(--main-tab-active-bg-color) !important;
    line-height: 1.6 !important;
    border-width: 1px !important;
    border-color: var(--main-tab-active-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    font-size: 11px !important;
    font-weight: 600 !important;
}
#btUrlText {
    display: none !important;
}
.statuspanel-label {
    -moz-appearance: none !important;
    background: var(--main-tab-active-bg-color) !important;
    border-width: 1px !important;
    border-bottom-width: 3px !important;
    border-bottom-color: var(--main-tab-active-bg-color) !important;
    border-color: var(--main-tab-active-bg-color) !important;
    color: var(--main-tab-active-text-color) !important;
    font-size: 12px !important;
    font-weight: 600 !important;
    padding: 2px 6px !important;
}
/* NEW TAB */

@-moz-document url("about:newtab") {
    /* BG */
    
    #newtab-scrollbox {
        background: var(--main-page-bg-color) !important;
        background-color: var(--main-page-bg-color) !important;
    }
    body {
        background: var(--main-page-bg-color) !important;
        background-color: var(--main-page-bg-color) !important;
    }
    /* SEARCH */
    
    #newtab-search-container {
        padding-top: 10px !important;
        padding-bottom: 20px !important;
    }
    #newtab-search-logo {
        display: none;
    }
    #newtab-search-icon {
        display: none;
    }
    #newtab-search-text {
        padding-left: 10px !important;
        -moz-appearance: none !important;
        background: var(--main-tab-active-bg-color) !important;
        border: 0px solid var(--main-page-border-color)!important;
        border-radius: 0px !important;
        outline: 0 none !important;
        box-shadow: none !important;
        font-size: 16px !important;
        color: var(--main-page-text-color) !important;
        font-family: var(--main-font-family) !important;
        transition-property: background;
        transition-duration: 150ms;
    }
    #newtab-search-text:focus,
    #newtab-search-text[autofocus] {
        background: var(--main-tab-active-hover-bg-color) !important;
        border: none!important;
        border-radius: 0px !important;
        transition-property: background;
        transition-duration: 150ms;
    }
    #newtab-search-submit {
        padding: 0px 15px 0px 15px !important;
        -moz-appearance: none !important;
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-tab-active-bg-color) !important;
        border: none!important;
        border-radius: 0px !important;
        box-shadow: none !important;
        font-family: var(--main-font-family) !important;
        font-size: 12px !important;
        text-transform: uppercase;
        color: var(--main-page-text-color) !important;
    }
    #newtab-search-text:focus + #newtab-search-submit,
    #newtab-search-text[autofocus] + #newtab-search-submit {
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-tab-active-hover-bg-color) !important;
        border: none !important;
        border-radius: 0px !important;
        box-shadow: none !important;
        color: var(--main-page-text-color-two) !important;
        font-family: var(--main-font-family) !important;
    }
    #newtab-search-text + #newtab-search-submit:hover,
    #newtab-search-text +#newtab-search-submit:hover:active {
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-page-text-color-two) !important;
        border: none!important;
        color: var(--main-page-text-invert-color-two) !important;
    }
    /* SITES */
    
    .newtab-site {
        box-shadow: none !important;
        border: none !important;
        color: var(--main-page-text-color-two) !important;
        margin: 0px;
        border-radius: 0px;
    }
    .newtab-cell:not([ignorehover]) > .newtab-site:hover,
    .newtab-site[dragged] {
        border: none;
        box-shadow: none;
        margin: 0px;
    }
    /* TITLES */
    
    .newtab-sponsored,
    .newtab-title,
    .newtab-suggested {
        background-color: var(--main-tab-active-bg-color) !important;
        border: 0px solid var(--main-page-border-color)!important;
        border-radius: 0px;
    }
    .newtab-sponsored,
    .newtab-title {
        font-size: 12px !important;
        font-family: var(--main-font-family) !important;
        color: var(--main-page-text-color) !important;
        transition-property: color;
        transition-duration: 150ms;
    }
    .newtab-sponsored {
        color: var(--main-page-text-color-two) !important;
    }
    .newtab-site:hover .newtab-title {
        background-color: var(--main-tab-active-hover-bg-color) !important;
    }
    /* THUMBNAILS */
    
    .newtab-thumbnail {
        border-radius: 0px !important;
        filter: grayscale(1) opacity(.9);
        border: 0px solid var(--main-theme-border-color)!important;
    }
    .newtab-site:hover .newtab-thumbnail {
        border-radius: 0px;
        transition: opacity 100ms ease-out;
        filter: grayscale(0) opacity(1);
        transition-property: filter;
        transition-duration: 50ms;
    }
    /* CELLS */
    
    .newtab-cell {
        background-color: var(--main-page-cell-bg-color);
        border-radius: 0px;
    }
    .newtab-cell:hover {
        background-color: var(--main-page-cell-bg-color);
        border-radius: 0px;
    }
    .newtab-cell:empty {
        outline: 1px dashed var(--main-theme-border-color);
        -moz-outline-radius: 0px;
    }
    /* CELLS */
    
    .newtab-cell,
    .newtab-intro-cell,
    .newtab-intro-cell-hover {
        background-color: var(--main-tab-active-bg-color);
        border-radius: 0px;
        border: 0px;
    }
    .newtab-cell:empty {
        outline: none;
        -moz-outline-radius: 0px;
        border: 0px solid white;
        border-radius: 0px;
    }
    /* SITES */
    
    .newtab-site {
        border: 0px solid white;
        border-radius: 0px;
        box-shadow: none;
        text-decoration: none;
        transition-property: top, left, opacity, box-shadow, background-color;
    }
    .newtab-cell:not([ignorehover]) .newtab-control:hover ~ .newtab-link,
    .newtab-cell:not([ignorehover]) .newtab-link:hover,
    .newtab-site[dragged],
    .newtab-intro-cell-hover .newtab-link {
        border: 0px solid white;
        border-radius: 0px;
        box-shadow: none;
        margin: 0px;
    }
    .newtab-cell,
    .newtab-control,
    .newtab-link,
    .newtab-cell,
    .newtab-link,
    .newtab-intro-cell-hover,
    .newtab-link {
        border: 0px solid white;
        border-radius: 0px;
        box-shadow: none;
    }
    /* CONTROLS */
    
    .newtab-control-block {
        opacity: 0;
        transition-property: opacity;
        transition-duration: 150ms;
    }
    .newtab-control-pin,
    .newtab-site[pinned] .newtab-control-pin:hover:active {
        opacity: 0;
        transition-property: opacity;
        transition-duration: 150ms;
    }
    /* UNDO */
    
    #newtab-undo-container {
        background: var(--main-page-text-color-two) !important;
        border-radius: 0px;
        border-color: transparent;
    }
    /* CUSTOMIZE */
    
    #newtab-intro-what {
        margin-top: -4px;
        font-size: 12px !important;
        font-family: var(--main-font-family) !important;
        color: var(--main-page-link-color);
        text-decoration: none;
    }
    #newtab-intro-what:hover {
        color: var(--main-page-link-color);
    }
    #newtab-customize-button,
    .newtab-customize {
        background: url(data:image/png;base64,) center no-repeat!important;
    }
}
/* HOME */

@-moz-document url("about:Home"),
url("about:home") {
    #sync, #searchLogoContainer, #searchEngineLogo, #restorePreviousSessionSeparator {
        display: none !important;
    }
    html {
        background-color: var(--main-page-bg-color) !important;
        background: var(--main-page-bg-color) !important;
        color: var(--main-page-text-color)
    }
    body {
        -moz-appearance: none !important;
        background: transparent !important;
        margin: 0;
        -moz-box-orient: vertical;
        width: 100%;
        height: 100%;
    }
    a {
        color: var(--main-page-link-color);
        text-decoration: none;
    }
    .searchSuggestionRow {
        color: rgb(0, 0, 0);
    }
    #searchForm {
        width: 500px;
    }
    /* SEARCH */
    
    #searchIcon {
        display: none !important;
    }
    #searchForm {
        border-radius: 4px !important;
    }
    #searchText {
        padding-left: 10px !important;
        -moz-appearance: none !important;
        background: var(--main-tab-active-bg-color) !important;
        border: 0px solid var(--main-page-border-color)!important;
        border-radius: 0px !important;
        outline: 0 none !important;
        box-shadow: none !important;
        font-size: 16px !important;
        color: var(--main-page-text-color) !important;
        font-family: var(--main-font-family) !important;
        transition-property: background;
        transition-duration: 150ms;
    }
    #searchText:focus,
    #searchText[autofocus] {
        background: var(--main-tab-active-hover-bg-color) !important;
        border: none!important;
        border-radius: 0px !important;
        transition-property: background;
        transition-duration: 150ms;
    }
    #searchSubmit {
        padding: 0px 15px 0px 15px !important;
        -moz-appearance: none !important;
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-tab-active-bg-color) !important;
        border: none!important;
        border-radius: 0px !important;
        box-shadow: none !important;
        font-family: var(--main-font-family) !important;
        font-size: 12px !important;
        text-transform: uppercase;
        color: var(--main-page-text-color) !important;
    }
    #searchText:focus + #searchSubmit,
    #searchText[autofocus] + #searchSubmit {
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-tab-active-hover-bg-color) !important;
        border: none !important;
        border-radius: 0px !important;
        box-shadow: none !important;
        color: var(--main-page-text-color-two) !important;
        font-family: var(--main-font-family) !important;
    }
    #searchText + #searchSubmit:hover,
    #searchText + #searchSubmit:hover:active {
        background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, padding-box, var(--main-page-text-color-two) !important;
        border: none!important;
        color: var(--main-page-text-invert-color-two) !important;
    }
    #snippets {
        display: inline-block;
        text-align: start;
        text-align: left;
        margin: 12px 0;
        color: var(--main-page-text-color);
        font-family: var(--main-font-family) !important;
        font-size: 14px;
    }
    #launcher,
    #restorePreviousSession {
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }
    .launchButton::before {
        display: none !important;
    }
    #launcher button {
        -moz-box-orient: vertical!important;
        background: none !important;
        min-width: 40px !important;
        min-height: 45px !important;
        max-height: 45px !important;
        font-family: "Segoe UI" !important;
        font-weight: normal!important;
        font-size: 10pt !important;
        color: var(--main-page-text-color) !important;
        border-radius: 0px !important;
        margin-right: 0px !important;
        margin-left: 0px !important;
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px !important;
        padding-top: 0px !important;
        padding-left: 15px !important;
        padding-right: 15px !important;
    }
    #launcher button:hover,
    #restorePreviousSession:hover {
        background: var(--main-tab-active-bg-color) !important;
