@namespace url(http://www.w3.org/1999/xhtml);

/* ドラクエ10極限攻略ブログ - 151013 */
@-moz-document domain("blog.kyokugen.info") {
  p {
    font-size: 90%;
  }
  #s,#searchsubmit {/* 検索窓 */
    background: #CCC none repeat scroll 0% 0%;
  }
  
  .sitename,/* header */
  .descr,/* header */
  #gazou,/* header */
  .smanone,
  p[style="color:#666;margin-bottom:5px;"],/* スポンサーリンク */
  .adsbygoogle,
  a[href^="http://blog.with2.net/"],
  div[id$="_main"],/* with2 投票 */
  #mybox .textwidget,/* R */
  #footer {
    display: none !important;
  }
}


/* ドラクエ10極限攻略データベース - 20150802 */
@-moz-document domain("ドラクエ10.jp") {
  .top_title {
    font-weight: normal;
    margin-bottom: 1em; 
  }
  .kyokugen {
    color: #000;
  }
  .col_1, .col_2, .col_3, .col_4, .col_5, .col_6, .col_7, .col_8, .col_9, .col_10, .col_11, .col_12 {
    float: right;
  }
  
  /* h1.top_title, */
  #pc_head1,/* ドラクエX攻略サイトといえば「極限」 */
  #pc_head2,/* SNS */
  #top_contents > .row:nth-of-type(1) .red_desc,
  #top_contents > .row > .b1 > i.fa.fa-exclamation-triangle,/* ※ */
  .ad,
  .sp_link {
    display: none !important;
  }
}


/* 某BBS用 */
@-moz-document domain("kyokugen.info") {
  body, td, th {
    font-family: "ヒラギノ角ゴ Pro W3", Hiragino Kaku Gothic Pro, "メイリオ", Meiryo, sans-serif;
  }
}
