@namespace url(http://www.w3.org/1999/xhtml);

/* まんまる堂 */
/* 最新記事一覧は記事ページのカテゴリの下　ブラウザで1段階縮小するのでfont-sizeなし */
@-moz-document domain("dq10maru.com") {
  .article-title {
    background: #E0F8E0;
  }

  #blog-header,
  #main-inner > b,
  ul[id^="ldblog_related_articles"] .related-article-image,
  .twitter-share-button,
  h3[style="padding:2px 18px;"],
  b+br,
  ins.adsbygoogle,
  .koukokuGoo,
  #rankingbanner,
  .socialBottom,
  #___gcse_0,
  .brp,
  .amazon,
  a[href^="http://blog.with2.net"],
  a[href^="http://game.blogmura.com"],
  a[href^="http://game-blog-ranking.com"],
  img[src="http://livedoor.blogimg.jp/manmarumarusuke/imgs/1/7/17cfec6d.png"],
  .plugin-twitter_profile,
  #sidebar-inner > :nth-of-type(1),/* 人気ブログランキング */
  #sidebar-inner > :nth-of-type(3),/* まんまる堂Games */
  #sidebar-inner > :nth-of-type(4),/* スポンサードリンク */
  #sidebar-inner > :nth-of-type(5),/* ニコニコ生放送のコミュニティ */
  #sidebar-inner > :nth-of-type(6),/* まんまる時計 */
  #sidebar-inner > :nth-of-type(8)/* 記事検索 */ {
    display: none !important;
  }
}
