@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozilla.org") {
  
    body{background: linear-gradient(to bottom  , rgba(0, 191, 255, 0.01) 23%, rgba(0, 0, 0,  0.45) 100%)!important;
  background-attachment: fixed !important;background-color: #000 !important;	background-clip: border-box !important;
	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}
     *{font-family:Constantia!important;color: #3ce !important}   
     .addon-details .header-bg, .home .header-bg, .primary, .secondary, .header-bg { background: transparent!important} 
  #tabzilla-wrapper, .highlight, img.icon, #main-wrapper, #tabzilla:before {background: transparent!important}
  
	/* лого */
	
	.site-title > a:nth-child(1) > img:nth-child(1), #tabzilla a{opacity:.0!important}
	#masthead{background: rgba(0, 0, 0,  0.35) url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/black_firefox_zpsthqnkmqb.png)!important; background-size: 13% !important;background-position: bottom left !important;background-repeat: no-repeat !important} 
	  
  /* Поиск */
	
	.header-search, #tabzilla-panel #tabzilla-nav ul li#tabzilla-search input {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;}
	.header-search input{background: rgba(0, 0, 0, 0.7)url(http://www.met.gov.om/opencms/export/system/modules/om.gov.met.website/resources/images/search.png)!important;background-size: contain !important;background-repeat: no-repeat !important;transition:all 2s ease-in-out !important}
  .header-search input:hover{box-shadow: 0px 0px 5px rgba(0, 191, 255, .7 )inset !important;transition:all 2s ease-in-out !important}
   button.search-button {background: rgba(0, 0, 0, 0.7)url(http://eseminar.dbp.gov.my/wacanasulalatus/images/Forward%20Arrow.png)!important;background-size: contain !important;background-repeat: no-repeat !important;transition:all 2s ease-in-out !important}
   button.search-button:hover{box-shadow: 0px 0px 5px rgba(0, 191, 255, .7 )inset !important;transition:all 2s ease-in-out !important}
  
  /**/
	
	.menu-nav em, .menu-nav > ul > li:hover:after, .hovercard.addon:hover .summary, #featured-addon, #monthly, #st-patricks, .previews .control.prev, .previews .control.next, .other-categories, #refine-results, .versions .primary > .warning, .featured, .listing-header, .listing-footer, .tab-wrapper .fm-control, #sorter > ul > li.extras:hover:after, #tabzilla:after{background:transparent!important}
	#site-nav > ul > li, #tabzilla-panel #tabzilla-nav ul li, #tabzilla-wrapper , .island > section, .other-categories, #refine-results, .versions .primary > .warning, .secondary .submit-theme, .featured, .listing-header, .listing-footer, .tab-wrapper .fm-control, .persona-preview [data-browsertheme], .listing .item .info, .listing .item .action{border:transparent!important}
	#site-nav a.complete-themes{background:transparent!important;border-color:#000!important}
	
	#tabzilla { background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/png/tab2_zpsd4ygtvch.png) repeat scroll 0% 0% transparent;   height: 44px;    width: 150px; }
	
	button.good, .button.good, .button.add, #promos .control:after, ol.pagination li a:hover, ol.pagination li a:focus, ol.pagination li a:active, a.button:link, a.button:visited, button, input[type="submit"], input[type="button"], #sorter > ul > li.extras:hover > a, #sorter > ul > li.extras ul, #sorter > ul > li.extras ul a:hover, .s-featured #side-nav .s-featured a, .s-followers #side-nav .s-followers a, .s-users #side-nav .s-users a, .s-downloads #side-nav .s-downloads a, .s-rating #side-nav .s-rating a, .s-created #side-nav .s-created a, .s-free #side-nav .s-free a, .s-paid #side-nav .s-paid a, #side-nav .selected, .hovercard:hover, #c-14 a {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important;transition:all 350ms ease-in-out !important}
	button.good:hover, .button.good:hover, .button.add:hover, a.button:link:hover, a.button:visited:hover, button, input[type="submit"], input[type="button"]:hover, #search-facets .facets .facet.active, #search-facets .facets .facet:hover  {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.3)!important;border:none!important;transition:all 350ms ease-in-out !important}	
			
	.hovercard.addon:hover, .hovercard.addon:hover .icon, .hovercard:hover .more {background: rgba(0, 22, 24, 0.85)!important; border:none!important;border-radius:10px 0px!important;transition:all 250ms ease-in-out !important}
		
	.secondary li a.selected, .secondary li a:hover, .menu-nav > ul > li:hover > a, .menu-nav > ul > li > ul a:hover  {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important;border-radius:10px 0px!important;transition:all 350ms ease-in-out !important}
	
	.island, .addons li > a, #featured-addon > div, #monthly > div, #go-mobile > div, .previews, .featured-inner, #collections-landing, .notice, #contribute-box textarea, .notification-box, .table-listing .item, .blocks .item, .listing .item, .separated-listing .item  {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important;border-radius:10px!important;border:transparent!important}
		
	#tabzilla-panel, #footer {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important;border:transparent!important}
	
	span.no-restart, span.featured{background: rgba(255, 125, 0, 0.5)!important;box-shadow: 0px 0px 5px rgba(0, 0, 0, .9 )inset !important;   -o-box-shadow: 0px 0px 5px rgba(0, 0, 0, .9 )inset !important;   -webkit-box-shadow: 0px 0px 5px rgba(0, 0, 0, .9 )inset !important}
	
  .menu-nav > ul > li > ul, .persona.persona-small.persona-hover:hover, .personas-grid li:hover, #contribute-box  {background: rgba(0, 0, 0, 0.9)!important;border:none!important}
  
  .site-title a:hover, .site-title a:focus {text-shadow:0px 0px 40px #3ce, 0px 0px 20px #39f, 0px 0px 10px #3ce!important;transition:all 450ms ease-in-out !important}
  

}
