css:
@-moz-document domain(voat.co) {
/*
  
  Please disable subverse CSS.
  
  !!****************!!
    Enable Nightmode
  !!****************!!

 */

#show-menu-button {
  /* Someone screwed up the CSS with this element */
  display: inline;
}  
html, body {
  background-color: #1d1f21;
  color: #c5c8c6;
}
#header, #sr-header-area, #sr-more-link {
  background-color: #1d1f21;
  color: #c5c8c6;
}
#header-container {
  background-color: #282a2e;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
}
#header-banner {
  min-width: 1200px;
  max-width: 1880px;
}

.tabmenu li.disabled a.contribute {
  background:  linear-gradient(0deg, #2d2f33, #1f2227, #2d2f33);
  background-color: transparent;
  border-color: #2f3238;
  border-radius: 5px;
  color: #a0a0a0;
}
.tabmenu li.disabled a.contribute:hover {
  background:  linear-gradient(0deg, #1f2227, #2d2f33, #2d2f33, #1f2227);
  background-color: transparent;
  border-color: #33353b;
  border-radius: 5px;
  color: #cacaca;
}
  .tabmenu li.selected a.contribute {
  background: none;
  background-color: #2d2f33;
  border-color: #33353b;
  border-radius: 5px;
  color: #cacaca;
}
.tabmenu li.selected a.contribute:hover {
  background: none;
  background-color: #2d2f33;
  border-color: #383a3e;
  border-radius: 5px;
  color: #cacaca;
}
.btn-whoaverse.contribute {
  background:  linear-gradient(0deg, #2d2f33, #1f2227, #2d2f33);
  background-color: transparent;
  border-color: #2f3238;
  border-radius: 5px;
  color: #a0a0a0;
}
.btn-whoaverse.contribute:hover {
  background:  linear-gradient(0deg, #1f2227, #2d2f33, #1f2227);
  background-color: transparent;
  border-color: #33353b;
  border-radius: 5px;
  color: #cacaca;
}

.btn-whoaverse {
  background:  linear-gradient(0deg, #2d2f33, #1f2227, #2d2f33);
  background-color: transparent;
  border-color: #2f3238;
  border-radius: 5px;
  color: #a0a0a0;
}

.btn-whoaverse:hover {
  background:  linear-gradient(0deg, #1f2227, #2d2f33, #1f2227);
  background-color: transparent;
  border-color: #33353b;
  border-radius: 5px;
  color: #cacaca;
}
#container {
  background-color: #1d1f21;
  color: #c5c8c6;
  border: none;
  min-width: 1024px;
  max-width: 1880px;
}
a {
  color: #5f89ac;
}
a:visited {
  color: #4f799c;
}
a:hover {
  color: #6f99bc;
}

.pagename > a {
  font-family: Impact;
  font-size: 36px;
  padding: 0px 0px 0px 0px;
}


#loadmorebutton {
  margin-top: 8px;
}

.alert-h1 {
  color: #369;
}
.alert-h2 {
  color: #cacacd;
  text-decoration: underline;
}
.alert-title {
  color: #cacacd;
  
}
.userstats-heading-1 {
  color: #369;
}
.commenttextarea {
  background: none;
  background-color: #282a2e;
  color: #fff;
}
.form-control {
  background: none;
  background-color: #212328;
  opacity: .33;
}
.form-control:focus {
  background: none;
  background-color: #212328;
  opacity: 1;
}
.form-control:hover {
  background: none;
  background-color: #212328;
  opacity: .75;
}
.md {

  max-width: 1800px;

}
.comment {
  margin-top: 3px;
  padding-top: 3px;
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;

  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  padding-right: 4px;

}

.comment .comment,
.comment .comment .comment ,
.comment .comment .comment .comment,
.comment .comment .comment .comment .comment ,
.comment .comment .comment .comment .comment .comment,
.comment .comment .comment .comment .comment .comment .comment ,
.comment .comment .comment .comment .comment .comment .comment .comment,
.comment .comment .comment .comment .comment .comment .comment .comment .comment ,
.comment .comment .comment .comment .comment .comment .comment .comment .comment .comment,
.comment .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment, 
.comment .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment .comment {
  /* HOLY SHIT - WHO DID THIS?! */
  margin: 0px;
  margin-top: 3px;
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;
  
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
}

.comment.thread > .comment.child {
  margin: 6px;
  margin-right: 4px;
  margin-bottom: 4px;
}
.md {
  background: none !important;
  background-color: transparent !important;
  border-color: #282a2e !important;
}

.md > blockquote {
  background: none !important;
  background-color: rgba(0,0,0,0.1) !important;
  border: 1px solid #1d1f21 !important;
  border-left: 3px solid #1d1f21 !important;

  margin-left: 2px !important;
}

.md > blockquote > p {
  color: #85bd69;
}

.md > blockquote > p::before {
  color: #85bd69;
  content: ">"
}


.submission.odd {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21 !important;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 3px;
  
}

 .submission.even {
  background: none;
  background-color: #282a2e;
  border: 1px solid #0d0f11 !important;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 3px;
}

.side {
  background: none;
  background-color: #282a2e;
  border: 1px solid #0d0f11;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 3px;
}


.score.unvoted {
  color: #9f9fac;
  
}

.side .spacer.spacersection {
  background: none;
  background-color: rgba(0,0,0,0.15);
}

.sidecontentbox .content {
  background-color: rgba(0,0,0,0.15);
}

.well {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;

  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
}

.userstats {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 6px;

}

.userstats-body {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 6px;
  padding-left: 4px;
  padding-right: 4px;
  padding-bottom: 4px;
}


.userstats-heading-2 {
  background: none;
  background-color: transparent;
  color: #cacaca !important;
  font-weight: 800;
  margin-top: 3px;
  margin-bottom: 2px;
  
}

.userstats-submission-title {
  color: #5f89ac;
}
.userstats-submission-title:hover {
  color: #6f99bc;
}


.alert-notice {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  margin-bottom: 20px;
}
.alert-featuredsub {
  background: none;
  background-color: transparent;
  border: 0px;
  padding-left: 0px;
  padding-right: 0px;
}


.col-md-6 {
  background: none;
  background-color: #282a2e;
  border: 1px solid #1d1f21;
  width: 49%;
  box-shadow: 1px 1px 1px rgba(0,0,0,0.5);
  padding-top: 3px;
}
div.row > div:nth-child(2) {
  margin-left: 2px;
}

div.row > div:nth-child(1) {
  margin-right: 2px;
}

/*
p.title > a.title.may-blank {
  color: #5f89ac;
  opacity: .9;
}
p.title > a.title.may-blank:hover {
  color: #5f89ac;
  opacity: 1;
}
p.title > a.title.may-blank:visited {
  color: #5f89ac;
  opacity: .3;
}
*/

a.title {
  /*#5f89ac*/
  color: #5f89ac !important;
}
a.title:hover {
  text-decoration: underline !important;
}

a.title:visited {
  color: #8f8f8f !important;
}
a.comments {
  color: #bcbcbc !important;
}
a.comments:visited {
  color: #7f7f7f !important;
}
.control-label {
  color: #acacac !important;
}
}
