css:
@-moz-document domain("eharmony.com") {
/* What-If Matches */
.flex-match {
	background-color:#4A4A4A;
}

.flex-match .front.face {
    background-color:#4A4A4A;
}

.flex-match .front.face a {
    color:white;
}

.match-item {
    height: 350px;
    margin: 8px 8px 28px;
    padding: 0;
    width: 280px;
}

/* Match Operations */

.match-item .block-match, .match-item .hide-match {
    bottom: -20px;
    color: #fff;
    background-color: #dadada;
    font-size: 13px;
    height: 20px;
    padding-bottom: 5px;
}

.match-item .hide-match:hover {
    color: #fff !important;
    background-color: #585858;
}

.match-item .block-match:hover {
    color: #fff !important;
    background-color: #be1010;
}

/* Garbage */
.blocked-match, div.whatif, div[class^="restart-upsell"] {
    display:none !important;
    position:absolute !important;
    left:0px;
    top:0px;
}


/* New Matches */
.unviewed-match {
    background-color:#3B813F;
}

.unviewed-match .front.face {
    background-color:#3B813F;
}

.unviewed-match .front.face a {
    color:white;
}


/* Cards */
.flipcard .face {
	box-shadow: 0 0px 0px #333;
	-webkit-box-shadow: 0 0px 0px #333;
}

/* Visitors */

.news-item:first-child {
    border-top: 0px solid #c2c2c2;
}

.news-item {
    border-bottom: 1px solid #ffffff;
    padding-top: 0;
}

.news-match-thumb {
    height: 200px;
    width: 200px;
}

.news-event {
    width: 340px;
}

.news-item-delete {
    display: block;
    top: 22px;
    background-color: #e8e8e8;
}

/* Messages */

#directory .image {
    height: 74px;
    margin: -12px 16px 0px -25px;
    width: 150px;
}

#directory .image, #directory figure {
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

#chat header {
    background: #fff;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,.2);
    height: 100%;
    left: 0;
    float: left;
    position: relative;
    top: 0;
    width: 208px;
    z-index: 1;
}

#chat header h1, #chat header h2 {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    padding-left: 8px;
}

#chat header ul {
    margin: 12px 0 0;
    padding-left: 8px;
    z-index: 0;
}

#chat header figure {
    background-position: center;
    background-size: initial;
    border-radius: 0;
    height: 100%;
    margin: 0 0px 0 0;
    width: 100%;
}

#chat header hgroup {
    padding: 0px;
}

#chat header figure.header-menu {
    position: absolute;
    bottom: 6px;
    right: 8px;
    cursor: pointer;
    float: none;
    height: 40px;
    width: 80px;
    border: 1px solid #328aa1;
    border-radius: 3px;
    box-shadow: none;
    color: #328aa1;
}

#chat header a:first-child {
    background-position: top;
    background-size: cover;
    border-radius: 0;
    float: left;
    height: 208px;
    margin: 0 0 16px 0;
    width: 208px;
}

#chat header a {
    text-decoration: none;
    display: inline-block;
}

#chat main {
    height: calc(100% - 182px);
    margin: -11px 0px 0px 0px;
    overflow-x: hidden;
    overflow-y: auto;
    padding: 16px;
    position: relative;
    top: 0px;
}

.user-gender-pref-female #chat header a {
    background-image: none;
}

#chat menu {
    background: #fff;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 3px;
    box-shadow: 0 5px 19px 0 rgba(0,0,0,.2);
    position: absolute;
    left: 40px;
    bottom: 46px;
    z-index: 2;
}

#chat footer .picker-btn {
    margin-left: 208px;
}

/* Unread */

#directory-list .unread {
    background: #328aa1;
    color: #d3e7ec;
}

#directory-list .unread h1 {
    color: #ffffff;
}

/* Selected */

#directory-list .selected {
    background: #e0eaef;
    color: #0f171b;
    cursor: default;
}

#directory-list .selected h1 {
    color: #213033;
}

/* Moved on */
#directory-list .disabled figure {
    opacity: 0.25;
}

#directory-list .disabled {
    cursor: default;
    background: #cccccc;
}

#directory-list .disabled .remove {
    color: #ffc1c6;
    background-color: #c02130;
    cursor: pointer;
    font-size: 1.3rem;
    font-weight: 600;
    height: 100%;
    line-height: 7.5rem;
    padding: 0 16px;
    position: absolute;
    right: 0;
    top: 0;
    width: 234px;
}
}
