css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://bakabt.me/"), url-prefix("http://bakabt.me/"), url-prefix("https://bakabt.me/description") {
  
    /*****************************************************/
   /*        BakaBT dark theme by w35 (2015-07)         */
  /*   An attempt to maintain the site's pink, light,  */
 /*          happy theme while making it dark.        */
/*****************************************************/
/*  Theme url: https://userstyles.org/styles/116352/ */
/*****************************************************/


  body:not(#description_iframe) {
    background-color: #0A000A !important;
  }
  #details, #userdetails, #browse {
    background: linear-gradient(to right, #250052 1%, #0A000A 5%, #0A000A 95%, #250052) fixed no-repeat !important;
  }
  body {
    color: #A5A5A5 !important;
  }
  body table .heading {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  td {
    border: 0px !important;
  }
  tr th{
    background: rgba(10,10,10,0.3) !important;
    border: 0px !important;
  }
  .content, #adopt .searchcloud {
    background: transparent !important;
    border: 0px !important;
    color: #A5A5A5 !important;
  }
  .content .pager {
    color: #A5A5A5 !important;;
  }
  .content .pager a, table.torrents thead a {
    color: #A5A5A5 !important;
  }
  .content .pager a:hover, .content .pager a.selected {
    color: #854C9E !important;
  }
  #tab_content thead, table.torrents thead {
    color: #A5A5A5 !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  input, .headerbar {
    color: #A0A0A0 !important;
    background: rgba(30,30,30,0.6) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  .headerbar ul, .torrent_mod ul, .torrent_mod li a {
    background: rgba(30,30,30,0.8) !important;
    border: 0px !important;
    color: #A5A5A5 !important;
  }
  .headerbar ul a:hover, .torrent_mod ul a:hover {
    color: #854C9E !important;
  }
  .headerbar span.icon.donator_black {
    transform: translateX(-5px) !important;
  }
  .torrent_mod, .torrent_mod.active{
    border: 0px !important;
  }
  a.title {
    color: #7AAAEE !important;
  }
  a:hover {
    color: #854C9E !important;
  }
  #offer_choice {
    background: transparent !important;
    border: 0px !important;
    color: #A5A5A5 !important;
  }
  #offer_choice a {
    background: rgba(10,10,10,0.6) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    color: #7AAAEE !important;
  }
  #offer_choice a:hover {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    color: #854C9E !important;
  }
  .code {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    color: #A5A5A5 !important;
  }
  #sandbox_form textarea{
    color: #A5A5A5 !important;
  }
  .note {
    background: rgba(10,10,10,0.6) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  tr.torrent.alt0, tr.torrent_alt.alt0 {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  tr.torrent.alt1, tr.torrent_alt.alt1 {
    background: rgba(10,10,10,0.3) !important;
    border: 0px !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }

    /*********/
   /*Signup.*/
  /*********/

  .suggestion, form td.alt1, form tr.alt1 {
    background: rgba(10,10,10,0.6) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  form td {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }

    /************/
   /*News page.*/
  /************/

  .news .comment_wrap, .column_B .column_B_body, .column_B .stats {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  .column_B .stats h1 {
    font-size: 50px
  }
  .column_B table.torrents tr {
    box-shadow: none !important;
  }

    /**************/
   /*Search page.*/
  /**************/
  /*  #browse   */

  #browse .browse_filter {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  .browse_filter a {
    color: #7AAAEE !important;
  }
  .browse_filter a:hover, #browse .torrents a:hover {
    color: #854C9E !important;
  }
  .browse_filter .searchcloud {
    background: rgba(10,10,10,0.6) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  #browse table.torrents{
    box-shadow: 0px 0px 5px #505050 !important;
  }

    /****************/
   /*Torrent pages.*/
  /****************/
  /*   #details   */

  #description_iframe {
    box-shadow: 0px 0px 5px #505050 !important;
  }
  .other-versions {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  li.quality-b, li.quality-b a {
    color: #7AAAEE !important;
  }
  #screenshots {
    background: rgba(30,30,30,0.0) !important;
    border: 0px !important;
  }
  #statistics{
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  #attachments, #filelist, #peerlist tbody {
    box-shadow: 0px 0px 5px #505050 !important;
  }
  #attachments table tr th, #peerlist table tr th {
    background: rgba(10,10,10,0.3) !important;
    border: 0px !important;
  }
  #attachments table tr:first-child, #peerlist table tr:first-child {
    box-shadow: 0px 0px 5px #505050 !important;
  }
  #attachments table th, #peerlist table th {
    color: #A0A0A0 !important;
  }
  #attachments table tr td, #peerlist table tr td {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
  }
  #filelist li.alt0, #peerlist tr.alt0{
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  #filelist li.alt1, #peerlist tr.alt1 {
    background: rgba(10,10,10,0.3) !important;
    border: 0px !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  .comment_wrap {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  .comment_wrap .quote {
    background: rgba(10,0,10,1) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  .infobox {
    background-color: black !important;
  }

    /*************/
   /*User pages.*/
  /*************/
  /*#userdetails*/

  /*Required transparencies.*/
  table.userstats, #tab_outer, #tab_content, li a {
    background: transparent !important;
    border: 0px !important;
  }
  #userdetails .comment_wrap {
    background: rgba(10,0,10,1) !important;
    border: 0px !important;
  }
  table.userstats td, div.box_grey {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  #tab_menu li a {
    color: #707070 !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  #tab_menu ul li.selected a, #tab_menu ul li a:hover {
    color: #854C9E !important;
  }
  #tab_outer a:hover, #tab_content thead th:hover {
    color: #854C9E !important;
  }
  #avatar tbody{
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
  }
  #avatar tbody td {
    border: 0px !important;
  }

    /*********/
   /*Top 10.*/
  /*********/

  tr.alt0 {
    background: rgba(30,30,30,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
  tr.alt1 {
    background: rgba(10,10,10,0.3) !important;
    border: 0px !important;
    box-shadow: 0px 0px 5px #505050 !important;
    text-shadow: 2px 1px 2px #000000 !important;
  }
}
