}

a.quotelink:visited,a.quotelink:visited:hover{
color:#b5f6ed!important;
}
}

@-moz-document url("https://www.4chan.org/") {
html {
  background: #2A2C2B;
}
#hd,#announce {
  display: none;
}

body {
  background: #2A2C2B;
  color: #2A2C2B;
}

.top-box {
  background: #374140;
}

#boards a {
  color: #D6DAF0;
  text-decoration: none;
}

h1, h2, h3, h4, h5, h6 {
  color: #7efddf;
}

.boxbar {
  background: none;
}

div#filter-container{display:none}

.box-outer {
  border: 0px solid;
  zoom: 100%;
  margin-bottom: .5em;
  padding-bottom: .5em;
}

.left-box .boxbar {
  background: #374140;
}

.left-box {
  background: #374140;
}

.right-box {
  background: #374140;
  color: #bbb;
}

.right-box .boxbar {
  background: #374140;
  color: #2A2C2B;
}

a:visited {
  color:#D6DAF0;
}

a.boardlink{color: #D6DAF0;}

#ft ul {display:none;}

#copyright{display:none;}
}
