css:
@-moz-document domain("justanimedubbed.tv") {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url('http://fonts.gstatic.com/s/opensans/v10/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2') format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

* {
  font-family: 'Open Sans' !important;
  font-weight: normal !important;
}

.logo_ara {
  height: 0px;
  width: 0px;
}

body {
  background: url("https://i.imgur.com/WNRv0U0.jpg") no-repeat fixed center center;
}

iframe {
  margin-left: 15%;
}

.heading {
  color: #FFF;
  background: -webkit-linear-gradient(top, #48729C,#193551);
}

#footer {
  background: 0;
  background-color: #151515;
  margin-bottom: 20px;
}

#left-column {
  margin-left: auto;
  margin-right: auto;
  width: 99%;
  float: none;
}

#heading-archive { 
  width: 98%; 
}

#right-sidebar, .affi-add, .in_up_ad-area, .copyright, .fornoobs {
  display: none;
}
}

@-moz-document regexp("http://www\\.justanimedubbed\\.tv/(?!watch).*") {
#left-column > div:nth-child(2), #left-column > div:nth-child(3) {
  display: none;
}

#unit > iframe {
  display: none !important;
}
}
