css:
@-moz-document domain("niicmusic.com") {
#navigation {
  background-color: rgb(23, 23, 23);
  padding: 2px;
  padding-left: 7px;
  border-radius: 5px;
  width: 896px;
  margin-left: 4%;
}

#navigation ul li a {
  color: #98B2E0;
}

#header h1 {
  color: #96D35F;
  padding-bottom: 0px;
  text-shadow: 2px 3px #000000;
}

#widget_region_left_sidebar {
  background-color: transparent;
}

#widget_region_left_sidebar h3 {
  background-color: transparent;
  color: #21427E;
  text-shadow: 1.5px 1.5px #777373;
}

#iframecode {
  margin-left: 4%;
  height: 250px !important;
}

body {
  background-color: #494949;
  background-image: none;
    background: -webkit-linear-gradient(left top, #147B11, #6A04BB);
    background: -o-linear-gradient(bottom right, #147B11, #6A04BB);
    background: -moz-linear-gradient(bottom right, #147B11, #6A04BB);
    background: linear-gradient(to bottom right, #147B11, #6A04BB);
  background-attachment: fixed;
}

a {
  text-decoration: none !important;
}

/* Nah */
#header h1 {
  background-image: none;
}

#widget_region_header {
	display: none !important;
}
}
