css:
@-moz-document domain("songza.com") {

.subnav, *[class^="browse-"], *[class^="static-"] {

    background-color: #212121 !important;

}



*[class^="browse-"], *[class^="fullplayer-"], .settings *, *[class$="-byline"] a {

    color: rgba(255, 255, 255, 0.8);

}



fieldset input[type="text"], input[type="password"], textarea {

    background-color: #060039 !important;

    border: 1px solid rgba(255, 255, 255, 0.8) !important;

}



h1, h2, h3, h4, h5, h6 {

    color: rgba(255, 255, 255, 0.8) !important;

}



.fullplayer-info { background: #00275f !important; }
  
  
/* Left Side "Mood" Text  */

.subnav-item a {
    color: rgba(220, 220, 220, 0.85) !important; }


  
.subnav-item a:hover {
    color: rgba(255, 255, 255, 1.9) !important;
} 
  

.subnav-item.selected a {
    color: #FFFFFF !important;
}    
  
  
    
    /* Browse Text */

.show-subnav .nav-item a:hover {
    background-color: rgba(220, 220, 220, 0.8) !important;
    color: #FFFFFF !important; } 
    
    
.show-subnav .nav-item.selected a {
    background-color: rgba(220, 220, 220, 0.7) !important;
    color: #FFFFFF !important;
}
    
    
.show-subnav .nav-item a {
    color: rgba(220, 220, 220, 0.99) !important;
    border-color: rgba(220, 220, 220, 0.1) !important;
}  
    
    
.show-nav .small .nav-item a, .show-subnav .small .nav-item a {
    color: rgba(255, 255, 255, 0.9) !important;
}    
    
    
    
    
    /* LOGO */

.logo a:hover {
    opacity: 0.8 !important;
}
.logo a {
    color: #FFFFFF !important; 
    text-decoration: none;
}
    
    
    
/*- Mini Player */

.miniplayer-info-playlist-title a {
    color: #F3F3F3 !important;
}    
    
    
.miniplayer-info-playlist-favorite-status {
    font-size: 2.6rem;
    color: #FFC684 !important;
}    
    
    
    
.miniplayer-timeline-current-time {
       background-color: rgba(224, 224, 224, 0.4) !important;
}   
    
    
    
   .miniplayer-info-track-title a, .track-history-track-title a {
    color: rgba(255, 255, 255, 0.95)!important;
} 
    
 .miniplayer-info-artist-name a, .track-history-artist-name a {
    color: rgba(229, 229, 229, 0.99)!important;
     

     } 
    
    
    
    .miniplayer-track-action.thumb-down, .miniplayer-track-action.thumb-up {
    color: #BFBFBF !important; 
    
}
    
    
    
    .browse-playlist:hover .browse-playlist-meta-item.favorite {
    color: #ED9C34!important;
}
    
   
    
    
    .browse-playlist-meta-item.similar a {
    color: #BFBFBF !important;
}
    
     
    
    
   
    
    
.searchbox {
   
    color: #FFF !important;
    background-color: #919191 !important;
    
}
 
  .show-subnav .searchbox input:focus, .show-subnav .show-search-results .searchbox input {
    color: #373737;
}
   
  .track-history-toggle {
      color: #CDCDCD !important;
}  
    
  
   .search-results {
    background-color: #141414 !important;
}   
    
  
 .active .search-wrapper, .show-search-results .search-wrapper {
    background-color: #141414 !important;
}
    
    
    
    
.welcome-message-wrapper {
   
    background-color: #707070 !important;
   
}    
    
    
.welcome-message {
    color: #C1C1C1 !important;
   
}    
    
a {
    
    color: #3A3A3A !important;
}    
    
   
  
}
