@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("codeby.net") {
  #content .pageContent {
  background: #F5F5F5;
  }
 
  #logoBlock {
   display: none !important;
  }
 
  #headerMover #headerProxy {
  height: 40px !important;
  }
 
html.is-setWidth .pageWidth {
    max-width: 1570px;
}
  .pageWidth {
   /* -o-transition: max-width .4s cubic-bezier(0.68, -0.55, 0.27, 1.55);
    -moz-transition: max-width .4s cubic-bezier(0.68, -0.55, 0.27, 1.55);
    -webkit-transition: max-width .4s cubic-bezier(0.68, -0.55, 0.27, 1.55);
    transition: max-width .4s cubic-bezier(0.68, -0.55, 0.27, 1.55);*/
}
 
  .discussionList .sectionHeaders{
  font-size: 8px;
  height: auto;
  }
 
  .discussionList .sectionHeaders a {
  visibility: hidden;
  }
  .stickyDelimeter span {
  visibility: hidden;
  }
 
  .discussionListItem {
  background-color: #DCDCDC;
  border-style: solid;
  border-color: #FFF #DDD #DDD;
  }
 
  .discussionListItem .stats {
  background-color: #DCDCDC;
  border: solid 1px white;
  border-bottom-width: 0px;
  border-top-width: 0px;
  }
  body .faint, body a.faint, body .faint a {
  color: #000000;
  }
 
  .discussionListItem .lastPostInfo .username {
  font-size: 10pt;
  }
.discussionListItem .posterAvatar .avatarContainer {
  display: block;
  position: relative;
  left: 0px;
  }
  footer {
  visibility: hidden !important;
  }
}
