css:
@-moz-document domain("v3rmillion.net") {
ul.menu li a,
ul.menu li a img,
ul.menu li,
#panel .links a,
input.textbox,
input.button,
textarea,
select,
.post_buttons a,
a.large_button {
	-webkit-transition-duration: .15s;
	-moz-transition-duration: .15s;
	-o-transition-duration: .15s;
	-ms-transition-duration: .15s;
	transition-duration: .15s;
}

input.button,
ul.menu img,
.pm_count,
#panel .links a,
.post_buttons a,
a.large_button {
        border-radius: 2px;
        -moz-border-radius: 2px;
        -webkit-border-radius: 2px;
}

h1,h2,h3,h4,h5,h6 {
        margin: 0 0 20px 0;
        padding: 0;
        font-weight: 500;
}

strong {
        font-weight: 500;
}

body {
	background: #202020;
        margin: 0;
	color: #a0a0a0;
	font-family: Roboto, Arial, Sans-Serif;
	font-size: 14px;
	text-align: center; /* IE 5 fix */
	line-height: 1.4;
}

a:link, a:visited {
	color: #09affc;
	text-decoration: none;
}

a:hover, a:active {
	color: #fff;
	text-decoration: none;
}

#header {
        background: #1a1a1a urltop left repeat-x;
        padding: 18px 0px 10px 0px;
        border-top: 1px solid #303030;
        border-bottom: 1px solid #303030;
        text-align: center;
        box-shadow: inset 0px 0px 20px #0a0a0a;
        -moz-box-shadow: inset 0px 0px 20px #0a0a0a;
        -webkit-box-shadow: inset 0px 0px 20px #0a0a0a;
}

#bridge {
        height: 40px; 
        margin-top: -1px;
        border-bottom: 4px solid #CD1818;
}

.wrap {
        width: 90%;
        margin: auto;
        position: relative;
}

#container {
	width: 90%;
	color: #a0a0a0;
	margin: auto auto;
	padding: 20px;
	text-align: left; /* IE 5 fix */
}

#content {
	/* FIX: Make internet explorer wrap correctly */
	width: auto !important;
}

ul.menu {
        height: 50px;
	padding: 0;
	color: #202020;
	text-align: center;
        position: absolute;
        left: 0;right:0;margin: 0px auto;
}

ul.menu li {
        display: inline-block;
}

ul.menu li a:link, ul.menu li a:visited {
 
        position: relative;
        display: block;
        height: 20px;
        padding: 10px 10px 14px 10px;
	
        color: #09affc;
	text-decoration: none;
}

ul.menu li:hover a {
        background: #CD1818;
	color: #fff;
	text-decoration: none;
}

ul.menu li a.active {
        background: #09affc;
        color: #fff;
}

ul.menu li a.active img {
        background: #fff;
}

ul.menu li:hover ul {
        display: block;
}

ul.menu li a:hover img, ul.menu li:hover a img {
        background: #fff;
}

ul.menu li a img {
        vertical-align: middle;
        margin-top: -2px;
        margin-right: 5px;
        padding: 3px;
}

ul.menu li ul {
        position: absolute;
        z-index: 99;
        display: none;
        padding: 0px;
box-shadow: 0px 1px 1px #202020;
}

ul.menu li ul li {
       display: block;
       float: none;
}

ul.menu li ul li a:link, ul.menu li ul li a:visited {
        position: relative;
        display: block;
        background: #CD1818;
        width: 170px;
        padding: 10px;
        color: #fff;
        text-align: left;
}

ul.menu li ul li a:hover {
        background: #BB1A1A;
}

#panel {
	background: #202020;
	color: #a0a0a0;
	padding: 0px;
        text-align: left;
}

#panel input.textbox {
       background: #303030;
       border: 1px solid #505050;
}

#panel input.textbox:focus {
        border: 1px solid #B31515;
        box-shadow: 0px 0px 3px #FF2121;
        -moz-box-shadow: 0px 0px 3px #FF2121;
        -webkit-box-shadow: 0px 0px 3px #fFF2121;
}

#panel input.remember_me {
	vertical-align: middle;
	margin-top: -1px;
}

#panel .links a {
	background: #808080;
        margin-left: 5px;
        padding: 5px 10px;
        color: #fff;
}

#panel .links a:hover {
        background: #606060;
}

#panel a .pm_count {
        background: #404040;
        padding: 2px 6px;
        color: #121;
}

table {
	color: #a0a0a0;
	font-size: 14px;
}

.tborder {
	width: 100%;
	margin: auto auto;
}

.thead {
	background: #CD1818;
	color: #fff;
}

.thead a:link, .thead a:visited {
	color: #202020;
	text-decoration: none;
}

.thead a:hover, .thead a:active {
	color: #9F1212;
	text-decoration: none;
}

.tcat {
	background: #303030;
	color: #a0a0a0;
	font-size: 11px;
}

.tcat a:link, .tcat a:visited {
	color: #fff;
}

.tcat a:hover, .tcat a:active {
	color: #a0a0a0;
}

.trow1, .trow2 {
	background: #202020;
        border-bottom: 1px solid #303030;
}

.trow_shaded {
	background: #303030;
}

.trow_selected td {
	background: #101010;
}

.trow_sep {
	background: #303030;
	font-size: 14px;
	font-weight: bold;
}

.tfoot {
	background: #9F1212;
	color: #fff;
}

.tfoot a:link, .tfoot a:visited {
	color: #202020;
	text-decoration: none;
}

.tfoot a:hover, .tfoot a:active {
	color: #9F1212;
	text-decoration: none;
}

#footer {
        background: #1a1a1a url top left repeat-x;
        padding: 18px 0px 10px 0px;
        border-top: 1px solid #303030;
        border-bottom: 1px solid #303030;
        box-shadow: inset 0px 0px 20px #0a0a0a;
        -moz-box-shadow: inset 0px 0px 20px #0a0a0a;
        -webkit-box-shadow: inset 0px 0px 20px #0a0a0a;
       text-align: left;
}

#footer ul {
        list-style-image: url;
        list-style-position:inside;
        display: inline-block;
        width: 20%;
        margin: 0px;
        padding: 0px;
}

#footer hr {
        background: #404040;
        height: 1px;
        margin: 20px 0px;
}

ul.navigation {
        display: block;
        list-style: none;
        background: #303030;
        height: 40px;
        margin: 0px;
        padding: 0px;
	color: #a0a0a0;
	font-size: 14px;
}

ul.navigation li:first-child {
        display: inline-block;  
        border-right: 20px solid transparent;  
        border-bottom: 40px solid #9F1212;   
        height: 0;  
        line-height: 40px;  
        background: #303030;
        padding: 0px 10px;
        color: #fff !important;
	text-decoration: none;
}

ul.navigation li:first-child a:hover {
        color: #202020;
}

ul.navigation li {
        display: inline;  
        height: 40px;  
        line-height: 40px;  
        padding: 0px;
}

ul.navigation li a:link, ul.navigation li a:visited {
        display: inline;  
        margin-right: 1px;
        color: #fff;
	text-decoration: none;
}

ul.navigation li a:hover, ul.navigation li a:active {
        color: #a0a0a0;
	text-decoration: none;
}

ul.navigation li.nav_active {
	color: #a0a0a0;
}

@media screen and (max-width: 1100px) {
        ul.navigation {
                height: auto;
                padding: 0px 0px;
}

ul.navigation li {
                float: none;
                display: inline;
}

ul.navigation li a:link, ul.navigation li a:visited {
                background: transparent;
                display: inline;  
                height: auto;  
                line-height: auto;  
                margin-right: 1px;
                padding: 0px 10px;
                color: #fff;
        	text-decoration: none;
}

ul.navigation li a:hover, ul.navigation li a:active {
                background: transparent;
                color: #a0a0a0;
        	text-decoration: none;
}

ul.navigation li.nav_active {
                background: transparent;
                border: none;
                display: block;  
                height: auto;  
                line-height: auto;  
                padding: 0px 10px;
                color: #fff;
        	text-decoration: none;
}

.navigation .active {
	        color: #a0a0a0;
	        font-weight: 500;
}


}

.smalltext {
	font-size: 12px;
}

.largetext {
	font-size: 16px;
	font-weight: bold;
}

input.textbox {
	background: #101010;
	color: #a0a0a0;
	border: 1px solid #303030;
	padding: 5px;
        font-family: open sans, arial, sans-serif;
}

input.textbox:focus {
        border: 1px solid #B31515;
        color: #fff;
}

input.button {
        background: #808080;
        padding: 5px 10px;
        border: none;
        color: #fff;
        font-family: roboto, arial, sans-serif;
        font-weight: 500;
        text-transform: uppercase;
}

input.button:hover {
        background: #606060;
        cursor: pointer;
}

textarea {
	background: #101010;
	color: #fff;
        border: 1px solid #303030;
	padding: 2px;
	font-family: Verdana, Arial, Sans-Serif;
	line-height: 1.4;
	font-size: 13px;
}

textarea:focus {
        border: 1px solid #B31515;
        box-shadow: 0px 0px 3px #FF2121;
        -moz-box-shadow: 0px 0px 3px #FF2121;
        -webkit-box-shadow: 0px 0px 3px #FF2121;
}

select {
	background: #101010;
        padding: 5px;
	border: 1px solid #303030;
        color: #a0a0a0;
}

select:focus {
        border: 1px solid #B31515;
        color: #fff;
}

.editor {
	background: #f1f1f1;
	border: 1px solid #ccc;
}

.editor_control_bar {
	background: #202020;
	border: 1px solid #303030;
}

.autocomplete {
	background: #fff;
	border: 1px solid #000;
	color: black;
}

.autocomplete_selected {
	background: #adcee7;
	color: #000;
}

.popup_menu {
	background: #ccc;
	border: 1px solid #000;
}

.popup_menu .popup_item {
	background: #fff;
	color: #000;
}

.popup_menu .popup_item:hover {
	background: #C7DBEE;
	color: #000;
}

.trow_reputation_positive {
        border-left: 5px solid #37d330;
}

.trow_reputation_negative {
        border-left: 5px solid #d33030;
}

.reputation_positive {
	color: #37d330;
}

.reputation_neutral {
	color: #606060;
}

.reputation_negative {
	color: #d33030;
}

.invalid_field {
        background: #101010;
        padding: 5px;
	border: 1px solid #d33030;
	color: #d33030;
}

.valid_field {
   
        background: #101010;
        padding: 5px;
	border: 1px solid #37d330;
        color: #37d330;
}

.validation_error {
	background: url no-repeat center left;
	color: #d33030;
	margin: 5px 0;
	padding: 5px;
	font-weight: 500;
	font-size: 11px;
	padding-left: 22px;
}

.validation_success {
	background: url no-repeat center left;
	color: #37d330;
	margin: 5px 0;
	padding: 5px;
	font-weight: 500;
	font-size: 11px;
	padding-left: 22px;
}

.validation_loading {
	background: url no-repeat center left;
	color: #555;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}

/* Additional CSS (Master) */
img {
	border: none;
}

.clear {
	clear: both;
}

.hidden {
	display: none;
	float: none;
	width: 1%;
}

.hiddenrow {
	display: none;
}

.selectall {
	background-color: #FFDADA;
	font-weight: bold;
	text-align: center;
}

.float_left {
	float: left;
}

.float_right {
	float: right;
}

.expcolimage {
	float: right;
	width: auto;
	vertical-align: middle;
	margin-top: 2px;
}

.expcolimage img {
        background: #9F1212;
        padding: 3px;
}

img.attachment {
	border: 1px solid #E9D6D6;
	padding: 2px;
}

hr {
	background-color: #000000;
	color: #000000;
	height: 1px;
	border: 0px;
}

#copyright {
	margin: 0;
	padding: 10px 0 0 0;
}

#debug {
	float: right;
	text-align: right;
	margin-top: 0;
}

blockquote {
	margin: 0;
	background: #303030;
	padding: 5px;
	border: 1px solid #404040;
}

blockquote cite {
	font-weight: bold;
	border-bottom: 1px solid #404040;
	font-style: normal;
	display: block;
	margin: 5px 0;
}

blockquote cite span {
	float: right;
	font-weight: normal;
}

blockquote cite span.highlight {
	float: none;
	font-weight: bold;
	padding-bottom: 0;
}

.codeblock {
	background: #303030;
	border: 1px solid #404040;
	padding: 5px;
}

.codeblock .title {
	border-bottom: 1px solid #404040;
	font-weight: bold;
	margin: 5px 0;
}

.codeblock code {
	overflow: auto;
	height: auto;
	max-height: 200px;
	display: block;
	font-family: Monaco, Consolas, Courier, monospace;
	font-size: 13px;
}

.subforumicon {
        margin: -2px 2px 0px 0px;
	border: 0;
	vertical-align: middle;
}

.separator {
	margin: 5px;
	padding: 0;
	height: 0px;
	font-size: 1px;
	list-style-type: none;
}

form {
	margin: 0;
	padding: 0;
}

.popup_menu .popup_item_container {
	margin: 1px;
	text-align: left;
}

.popup_menu .popup_item {
	display: block;
	padding: 3px;
	text-decoration: none;
	white-space: nowrap;
}

.popup_menu a.popup_item:hover {
	text-decoration: none;
}

.autocomplete {
	text-align: left;
}

.subject_new {
	font-weight: bold;
}

.highlight {
	background: #FFCDCD;
	padding-top: 3px;
	padding-bottom: 3px;
}

.pm_alert {
	background: #2eb72b;
	text-align: center;
	padding: 10px;
	margin-bottom: 15px;
        color: #fff;
}

.pm_alert a:link, .pm_alert a:visited {
       color: #202020;
}

.pm_alert a:hover {
       color: #404040;
}

.red_alert {
background: #303030;
	border: 3px solid #9F1212;
	color: #A5161A;
	text-align: center;
	padding: 5px 20px;
	margin-bottom: 15px;
	border-radius: 10px;
width:auto;
}

.high_warning {
	color: #CC0000;
}

.moderate_warning {
	color: #F3611B;
}

.low_warning {
	color: #AE5700;
}

div.error {
	padding: 10px;
	background: #202020;
        border: 2px dashed #CD1818;
}

div.error p {
	margin: 0;
	color: #a0a0a0;
	font-weight: normal;
}

div.error p em {
	font-style: normal;
	font-weight: bold;
	padding-left: 24px;
	display: block;
	color: #CD1818;
	background: url no-repeat 0;
}

div.error ul {
	margin-left: 24px;
}

.online {
	color: #15A018;
}

.offline {
	color: #C7C7C7;
}

.pagination {
	font-size: 12px;
	padding-top: 10px;
	margin-bottom: 5px;
}

.tfoot .pagination, .tcat .pagination {
	padding-top: 0;
}

.pagination .pages {
	font-weight: bold;
}

.pagination .pagination_current, .pagination a {
	padding: 4px 8px;
	margin-bottom: 3px;
}

.pagination a {
	background: #606060;
}

.pagination .pagination_current {
	background: #CD1818;
	font-weight: bold;
	color: #fff;
}

.pagination a:hover {
	background: #404040;
	text-decoration: none;
}

.thread_legend, .thread_legend dd {
	margin: 0;
	padding: 0;
}

.thread_legend dd {
	padding-bottom: 4px;
	margin-right: 15px;
}

.thread_legend img {
	margin-right: 4px;
	vertical-align: bottom;
}

.forum_legend, .forum_legend dt, .forum_legend dd {
	margin: 0;
	padding: 0;
}

.forum_legend dd {
	float: left;
	margin-right: 10px;
}

.forum_legend dt {
	margin-right: 10px;
	float: left;
}

.success_message {
	color: #00b200;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}

.error_message {
	color: #C00;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}

.post_body {
	padding: 10px 0px;
}

.post_content {
	padding: 5px 10px;
}

.post_buttons a {
        background: #808080;
        padding: 5px 10px;
        color: #fff;
}

.post_buttons a:hover, a.large_button:hover {
        background: #606060;
        color: #fff;
}

a.large_button {
        background: #808080;
        padding: 10px 15px;
        color: #fff;
}

.quick_jump {
	background: url no-repeat 0;
	width: 13px;
	height: 13px;
	padding-left: 13px; /* amount of padding needed for image to fully show */
	vertical-align: middle;
	border: none;
}

.repbox {
	font-size:16px;
	font-weight: bold;
	padding:5px 7px 5px 7px;
}

._neutral {
	color: #606060;
	border:1px solid #606060;
}

._minus {
	color: #d33030;
	border:1px solid #d33030;
}

._plus {
	color: #37d330;
	border:1px solid #37d330;
}

.pagination_breadcrumb {
	background-color: #f5f5f5;
	border: 1px solid #fff;
	outline: 1px solid #ccc;
	padding: 5px;
	margin-top: 5px;
	font-weight: normal;
	white-space: nowrap;
}

.pagination_breadcrumb_link {
	vertical-align: middle;
	cursor: pointer;
}

.icon {
        vertical-align: middle;
        margin-top: -1px;
}

a.isQuote {
        background: #fff;
        color: #202020;
}

a.isQuote:hover {
        backgorund: #a0a0a0 !important;
        color: #202020;
}

.spoiler_header {
    background: #202020;
    border: 1px solid #CCC;
    padding: 4px;
    margin: 4px 0 0 0;
    color: #FFF;
}

.spoiler_body {
    background: #09affc;
    padding: 4px;
    border: 1px solid #CCC;
    border-top: 0;
    color: #FFF;
    margin: 0 0 4px 0;
}
   
