css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duolingo.com") {

html, body{

  background: #343434;

  color: #999;

}



.box-colored.white,

.player-container,

main:not(.full-width) .page-main,

main.full-width.main-right section.page-main.main-right,

main.full-width.main-right section.page-sidebar.sidebar-left{

  background: #282828;

}



.hint-popover .inner:before, .hint-popover .inner:after,

.hint-popover .inner{

  background: #333;

}



.hint-popover .inner::before,

.hint-popover .inner{

      box-shadow: -1px 1px #000;

}



.select-images.hover-effect>li{

  border-color: #444;

}

      

.list-lesson-item.past .inner,

.select-images>li,

.text-to-translate,

textarea{

  background: #222;

}



textarea{

  background: #222 !important;

  color: #aaa;

}

      

.list-lesson-item.locked .inner,

.player-footer{

  background: #444;

}



.progress-bar-dynamic.strength-bar,

.progress-bar-dynamic.strength-bar .skill-icon-container .challenge-num-circle.white,

main.full-width.main-right section.page-sidebar.sidebar-left,

.box-colored.light-gray,

.skill-icon.white{

  background-color: #444;

}

      

.skill-progress-headline,      

h1,h2,h3,h4,h5,h6{

    color: #aaa;

}



.skill-progress-headline span,

.skill-badge-name{

  color: #666;

}





::-webkit-scrollbar {

    width: 11px;

}



::-webkit-scrollbar-track {

  background: #444 !important;

}



::-webkit-scrollbar-thumb {

    border-radius: 4px;

    background: rgba(0,0,0,0.5) !important;

}



::-webkit-scrollbar-thumb:hover{

  border: 2px solid #006493 !important;

}



::-webkit-scrollbar:window-inactive {

    visibility: hidden;

}








body.background-img{
  background:#303030 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC) repeat!important;
}
  .bonus-row {
    background: #212121 none repeat scroll 0% 0%!important;
border-top: 2px solid #1A1A1A!important;
border-bottom: 2px solid #2F2F2F!important;
  }
  .box-gray{
    border: 2px solid #383838!important;
background: #212121 none repeat scroll 0% 0%!important;
  }
  .btn,.btn-white {
    -moz-appearance: none;
    border: 1px solid rgb(0,0,0);
    box-shadow:  0px 1px rgba(255, 255, 255, 0.1) inset, 0px 1px 3px rgba(0, 0, 0, 0.5);
    background: linear-gradient(rgb(50,50,50), rgb(40,40,40));
    color: rgb(166,166,166);
    text-shadow: none;
  }
  .btn:hover{
    background: linear-gradient(rgb(70,70,70), rgb(60,60,60));
        color: rgb(255,255,255);
        text-shadow: none;
        transition: .2s ease-in;
    
  }
  .btn:focus{
    color :rgb(0,173,238);
        text-shadow: 0 0 3px rgb(0,173,238);
        transition: .2s ease-in;
  }
  .topbar-blue{
    box-shadow:  0px 1px rgba(255, 255, 255, 0.1) inset, 0px 1px 3px rgba(0, 0, 0, 0.5);
    background: linear-gradient(rgb(50,50,50), rgb(40,40,40));
    color: rgb(166,166,166);
    border:none;
    
  }
  .session-end-footer {
    background: #151515 none repeat scroll 0% 0%!important;
  }
  .carousel-dot{
    
    background: #383838 none repeat scroll 0% 0%!important;
  }
  #app.correct .player-footer {
  border-top: medium none;
background: #212121 none repeat scroll 0% 0%;
    color: #7EB530;}
  textarea, .fake-textarea {
    color: #A5A5A5;
  }
  .fake-textarea, .fake-input {
    box-shadow: none;
    background: #212121 none repeat scroll 0% 0%;}
  .textarea-translate, .input-listen[type="text"], .listen-input, .one-line-input[type="text"] {
    border: 2px solid #3B3B3B;}
}
