css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("voat.co") {

#container {
    border-style: none;
}

.arrow-downvote,.arrow-downvoted,.arrow-upvote,.arrow-upvoted {
    background: url(http://i.imgur.com/OPcduHO.png);
    cursor: pointer;
    height: 16px;
    margin: auto;
    width: 16px;
    outline-style: none;
}

.arrow-upvote {
    background-position: 0 -12px;
}

.arrow-upvoted {
    background-position: -16px -12px;
}

.arrow-downvote {
    background-position: 0 -28px;
}

.arrow-downvoted {
    background-position: -16px -28px;
}

.arrow-upvote,.arrow-upvoted {
    margin-bottom: 3px;
}

.arrow-downvote,.arrow-downvoted {
    margin-top: 3px;
}

.comment .arrow-downvote,.comment .arrow-downvoted,.comment .arrow-upvote,.comment .arrow-upvoted {
    background: url(http://i.imgur.com/7QUA8nW.png);
    height: 12px;
    width: 12px;
}

.comment .arrow-upvote {
    background-position: -32px -12px;
}

.comment .arrow-upvoted {
    background-position: -44px -12px;
}

.comment .arrow-downvote {
    background-position: -32px -24px;
}

.comment .arrow-downvoted {
    background-position: -44px -24px;
}

#container, #header-banner {
    max-width: none;
}

#header {
    height: 52px;
    margin-bottom: 2px;
    margin-top: 0;
}

#header-account {
    padding-bottom: 0;
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
}

#header-banner {
    background-color: #cee3f8;
    border-bottom-width: 1px;
    border-color: #5f99cf;
    border-left-width: 0;
    border-right-width: 0;
    border-style: solid;
    border-top-width: 1px;
    height: 33px;
}

#header-container {
    border-style: none;
    height: 32px;
}

#header-img-a img {
    display: none;
}

#header-img-a::after {
    background-image: url(http://i.imgur.com/Rpmqy7l.png);
    background-repeat: no-repeat;
    content: '';
    display: block;
    height: 33px;
    width: 86px;
}

#mail {
    display: inline-block;
    overflow: hidden;
    position: relative;
    text-indent: -9999px;
    top: 2px;
}

#mail.havemail {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -18px 0;
    cursor: pointer;
    height: 12px;
    width: 18px;
}

#mail.nohavemail {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: 0 0;
    cursor: pointer;
}

#manage {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -55px 0;
    cursor: pointer;
    display: inline-block;
    height: 17px;
    overflow: hidden;
    position: relative;
    text-indent: -9999px;
    top: 4px;
    width: 18px;
}

#sr-header-area {
    border-style: none;
}

#sr-more-link {
    background-color: #f0f0f0;
    font-size: 15px;
    font-weight: bold;
    letter-spacing: 0;
    padding-right: 0;
}

#view-images {
    margin-left: -9px;
}

.btn-whoaverse.btn-block.contribute.submit-link {
    background: #ffffff none repeat-x scroll center left;
    background-image: url(http://i.imgur.com/Xzlc48P.png);
    background-position: 0px -278px;
    background-repeat: repeat;
    border: 1px solid #c4dbf1;
    display: block;
    font-size: 150%;
    font-weight: bold;
    height: 29px;
    letter-spacing: -1px;
    line-height: 20px;
    position: relative;
    text-align: center;
}

.btn-whoaverse.btn-block.contribute.submit-link:hover,.mlh {
    background-image: url(http://i.imgur.com/Xzlc48P.png);
    background-position: 0px -243px;
    background-repeat: repeat
;
    border-color: #879eb4;
}

.btn-whoaverse.btn-block.contribute.submit-text {
    background: #ffffff none repeat-x scroll center left;
    background-image: url(http://i.imgur.com/Xzlc48P.png);
    background-position: 0px -278px;
    background-repeat: repeat;
    border: 1px solid #c4dbf1;
    display: block;
    font-size: 150%;
    font-weight: bold;
    height: 29px
;
    letter-spacing: -1px;
    line-height: 20px;
    position: relative;
    text-align: center;
}

.btn-whoaverse.btn-block.contribute.submit-text:hover,.mlh {
    background-image: url(http://i.imgur.com/Xzlc48P.png);
    background-position: 0px -243px;
    background-repeat: repeat
;
    border-color: #879eb4;
}

.comment {
    border-color: #c4c4c4;
    border-style: solid;
    border-width: 1px;
    padding-bottom: 8px;
    padding-right: 6px;
    padding-top: 9px;
}

.expando-button.selftext.collapsed {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: 0px -44px;
}

.expando-button.selftext.collapsed:hover {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -32px -44px;
    height: 32px;
    width: 32px;
}

.expando-button.selftext.expanded {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -64px -44px;
    height: 32px;
    width: 32px;
}

.expando-button.selftext.expanded:hover {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -64px -44px;
    height: 32px;
    width: 32px;
}

.expando-button.selftext.loading,.expando-button.selftext.loading:hover {
    background: url(http://i.imgur.com/OPcduHO.png);
    background-position: -32px -76px;
    height: 32px;
    width: 32px;
}

.link .flat-list.buttons {
    margin-top: 1px;
}

.link a.title.may-blank {
    color: #0000ff;
}

.tabmenu .submit-link {
    display: none;
}

.tabmenu .submit-text {
    display: none;
}

a.author.may-blank {
    color: #0079d3;
}

a.author.may-blank.userinfo.tooltipstered {
    color: #617ec2;
    font-size: 12px;
    font-weight: bold;
}

a.btn-whoaverse.btn-block.contribute.submit-link:hover {
    color: #ffffff;
}

a.btn-whoaverse.btn-block.contribute.submit-text:hover {
    color: #ffffff;
}

a.comments.may-blank {
    color: #5e5e5e;
    border-style: none;
    background-color: transparent;
    font-weight: bold;
}

a.contribute.submit-link {
    margin-bottom: 0;
    margin-top: 0;
    padding-bottom: 2px;
    padding-left: 3px;
    padding-right: 3px;
    padding-top: 2px;
}

a.contribute.submit-text {
    margin-bottom: 0;
    margin-top: 0;
    padding-bottom: 2px;
    padding-left: 3px;
    padding-right: 3px;
    padding-top: 2px;
}

a.helplink.message-moderators-temporary-class {
    color: #0079d3;
}

a.subverse.hover.may-blank {
    color: #617ec2;
    font-size: 12px;
    font-weight: normal;
}

a.thumbnail.may-blank {
    margin-right: 4px;
}

a.thumbnail.may-blank img {
    border-bottom-width: 2px;
    border-color: #5f99cf;
    border-left-width: 2px;
    border-right-width: 2px;
    border-style: none;
    border-top-width: 2px;
    width: 50px;
}

a.togglebutton.savelink {
    color: #5e5e5e;
    border-style: none;
    background-color: transparent;
    font-weight: bold;
    padding-left: 10px;
}

a.upvoatsGiven {
    color: #33669f;
    font-weight: normal;
}

a.userkarma {
    color: #33669f;
}

a.userstats-submission-title {
    color: #0000ff;
}

body.light {
    background-color: #ffffff;
}

div span a {
    color: #33669f;
}

div.alert-featuredsub {
    display: none;
}

div.alert-h1 {
    color: #525252;
}

div.alert-h2 {
    color: #000000;
    font-size: 21px;
}

div.alert-title {
    color: #000000;
}

div.bottom p a {
    color: #0000ff;
}

div.footer-container {
    display: none;
}

div.logged-in {
    background-color: #eff7ff;
    margin-bottom: 0;
    padding-bottom: 0;
}

div.logged-in a {
    color: #33669f;
}

div.md p a {
    color: #617ec2;
}

div.score.likes {
    color: #ff8b60;
}

div.scorebar {
    display: none;
}

div.scorebar-likes {
    display: none;
}

div.side {
    height: 176px;
    padding-right: 8px;
}

div.sidecontentbox.content p a {
    color: #0000ff;
}

div.sitetable a {
    color: #043dd9;
    font-weight: normal;
}

div.sitetable.nestedlisting {
    width: 800px;
}

div.sr-list {
    background-color: #f0f0f0;
}

div.submission.even.link.stickied {
    display: none;
}

div.thread.comment a {
    color: #0000ff;
}

div.userstats-body p a {
    color: #617ec2;
    font-weight: bold;
}

div.userstats-heading-1 {
    color: #000000;
}

div.width-clip {
    background-color: #f0f0f0;
    height: 21px;
}

h1.alert-h1 {
    color: #0079d3;
}

html {
    overflow-x: hidden;
}

li.btn-whoaverse-paging a {
    background-color: #eeeeee;
}

li.disabled a {
    background-color: #eff7ff;
}

p.title {
    margin-top: -4px;
}

span.domain a {
    color: #888888;
}

span.hover.pagename {
    margin: 0;
    padding: 0;
}

span.hover.pagename a {
    color: #5a5a91;
    font-style: normal;
    font-variant: small-caps;
    font-weight: bold;
}

span.post_downvotes {
    color: #0000ff;
    font-weight: normal;
}

span.post_upvotes {
    color: #0000ff;
    font-weight: normal;
    display: none;
}

span.promoted {
    background-color: #5f99cf;
    border-style: none;
}

span.user a {
    color: #33669f;
}

textarea.commenttextarea {
    width: 66%;
}

ul li a {
    color: #0000ff;
}

ul.flat-list.buttons li {
    margin-top: -4px;
    padding-left: 0;
    padding-right: 0;
}

ul.flat-list.sr-bar.hover li a {
    color: #000000;
}

ul.tabmenu {
    display: none;
    height: 76%;
}

a.title.may-blank {
    font-weight: normal;
    padding: 0;
}

.even.link {
    background-color: #ffffff;
}

span.commentvotesratio {
    display: none;
}

#header-img-a {
    margin-bottom: 5px;
}

div.markdownEditorMainMenu {
    display: none;
}

a.togglebutton {
    color: #606060;
}

ul.flat-list.buttons li a {
    color: #606060;
}

p.tagline time {
    color: #919191;
}

a.btn-whoaverse.btn-block {
    color: #617ec2;
    font-weight: bold;
}

a.inline-loadcomments-btn {
    color: #617ec2;
    font-weight: bold;
}

div.submission {
    width: 887px;
    margin-top: -10px;
}

a.thumbnail.nsfw.may-blank {
    width: 50px;
    height: 50px;
}

.side {
    max-height: 700px;
    overflow-y: scroll;
}

}
