css:
@-moz-document url-prefix("http://www.zhihu.com/") {
/* i really want this to be global */
body.zhi{
	padding-top:55px;
     	color:#34495e;
  line-height: 2;
    font-size:16px;
   font-family:microsoft yahei;
}
body.zhi h2{
	  font-size: 18px;
  font-weight: 700;
}
.zm-item-vote {
  left: -68px;
  top: 10px;
}
.zu-top {
    box-shadow:none;
    background-image:none;
    border-bottom:none;
    background-color: #34495e;
    padding:5px 0;
}
.zu-top-add-question{
	background-image:none;
   border-radius:5px;
   background-color: #4e6d8d;
   border:none;
}
.zu-top-search-input{
	 border-radius:5px;
    border:none;
    box-shadow:none;
}
.top-nav-dropdown li:last-child>a{
	border-radius:0 0 5px 5px;
}
.top-nav-dropdown li:first-child>a {
  border-radius: 5px 5px 0 0 ;
}
*[class*="zg-btn"]{
	border-radius:5px;
   background-image:none;
   box-shadow:none;
}
.zu-top-nav-li.current{
   background-image:none;
    background:#34495e;
    color:#16a085;
}
.zu-top-search-input:focus{
    background-color: #293a4a;
    color: #1abc9c;
  	border:none;
    box-shadow:none;
}
.current .zu-top-nav-link,.current .zu-top-nav-link:visited,.current .zu-top-nav-link:active{
    color:#1abc9c;
}
a{
	color:#2c3e50;
}
.zu-top-search-input{
    color: #536a81;
  background-color: #293a4a;
}
.zu-top-nav-userinfo.selected, html.no-touch .top-nav-profile:hover .zu-top-nav-userinfo{
	background:#1abc9c;
    border-radius: 5px;
}
.top-nav-dropdown li a{
	border-top:1px solid #2f4154;
}
.top-nav-profile a{
	background:#34495E;
}
.top-nav-profile .top-nav-dropdown{
padding-top:10px
}
html.no-touch .top-nav-dropdown a:hover {
  background-color: #1ABC9C;
}
.zm-side-nav-link.active, html.no-touch .zm-side-nav-roundtable .follow-link:hover+.zm-side-nav-link, html.no-touch .zm-side-nav-link:hover{
  background: #ecf0f1;
  border-radius: 5px;
}
.zg-wrap {
  width: 1360px;
  margin: 0 auto;
  clear: both;
}
html.no-touch .zm-votebar .up:hover, html.no-touch .zm-votebar .down:hover, .zm-votebar .up.pressed, .zm-votebar .down.pressed {
  background: #2980b9;
  color: #fff;
}
.feed-item .avatar, .feed-item .avatar a, .feed-item .avatar img{
	width:75px;
    height:75px;
}
.feed-item .avatar img{
	border-radius:50%
}
.feed-main,.zm-item-answer {
  margin-left: 90px;
}
.zh-backtotop{
	  margin-left: 400px;
}
.origin_image{
	 width: 250px!important;
 	 height: auto!important;
}
}
