css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.teleopti.com") {

#outer-wrap{
  background-image: url('http://www.netanimations.net/fire-under-construction-animation.gif');
  background-repeat: no-repeat;
  background-position: right top;
}

.wide-container{
  background: none;
}

h1, h2, h4, h5, .slides li .headline, .big-box h1, .big-box h2 {
  font-family: 'Times New Roman';
}

.pure-col-1-4 {
  background-image: url('http://images5.fanpop.com/image/photos/30600000/Banana-gif-bananas-30667445-140-140.gif');
  background-repeat: no-repeat;
  background-position: bottom right;
}

#footer {
  background-image: url('http://nullroute.eu.org/mirrors/xkcd-geocities/imgs.xkcd.com/counter.gif');
  background-repeat: no-repeat;
  background-position: center bottom;
}

a:link{
color:#000066;
}

body{
background-color:#FFFF00;
font-family:Comic Sans MS,Comic Sans;
background-image:url("http://thumbs.dreamstime.com/z/texture-green-circuit-board-background-35084813.jpg");
font-size:15px;
color:#000000;
}

p{
background-color:#00FF00;
color:#FFFFFF;
background-image:none;
font-family:"Courier New", Courier, monospace;
font-size:20px;
}


}
