css:
@-moz-document url-prefix("https://www.academiadasapostas.com") {

 body, html {background:#2C2C2C !Important;}
p{color:#F0F0F0;}
#topmenu{  background: #3C3C3C!important;}
blockquote{background: #3C3C3C!important;color:#F0F0F0!important;}


.post_wrapper{  background: #3C3C3C!important;color:#F0F0F0;}

.catbg,.catbg span{background:#3C3C3C!important;color:#F0F0F0;}


#f h3.catbg a, #f h3.catbg, #f .table_list tbody.header td, #f .table_list tbody.header td a,#f .content strong {color: #E4E4E4;}

/*menu*/
.dropdown li span {color:#F0F0F0;text-shadow: 0 1px 1px #F0F0F0;}
.dropdown li:hover span {color:#3C3C3C; text-shadow: 0 1px 1px #050505;}
/*submenu hover*/
#topmenu .dropdown .submenu div, #topmenu .dropdown .submenu div li a {background:#2C2C2C!important;color:#F0F0F0; text-shadow: 0 1px 1px #3C3C3C;color:#F0F0F0;}

/*conteudo*/
.windowbg,.windowbg2,.pagesection, .windowbg span,.windowbg2 span{background:#383B34!important;}

/*links conteudo*/
#f a:link{color:#B2BFB7;}




/*sitemap-navegaçao*/
.navigate_section span{color:#54B054;}


/*jogos live*/
tbody {background: #3C3C3C;}
 .team-a p,.team-b p{color:#F0F0F0; }
.livescores .competition-today tr.even, .zapping .competition-today tr.even, .survivor table tr.even {background:#2C2C2C !Important; }
.livescores .competition-today td.status, .livescores .competition-today th.status, .livescores .competition-today td.hour, .livescores .competition-today th.hour .livescores a, .zapping a {color:#F0F0F0;}
.zapping .competition-today td.hour, .zapping .competition-today th.hour {color:#F0F0F0;}
.livescores a, .zapping a {color:#F0F0F0; }

/*users online*/
#f dl#ic_recentposts dd {color:#54B054; }
#f #upshrinkHeaderIC,h4.titlebg  {background: #3C3C3C;}

/*stiky*/
#f .topic_table td {background: #264A34;color:#54B054; }



/*entry*/
.entry  {background: #3C3C3C;  text-shadow: 0 1px 1px #3C3C3C;color:#F0F0F0;}

}
