css:
@-moz-document domain("producers-and-traders.de") {


/* ----- Producers & Traders Stylesheet ----- */
/* --- http://www.producers-and-traders.de ---*/


/* Hintergrundbild */
html{
    /*[[hintergrund_komplett]]*/
}

/* Schriftart */
body, td, th {
  font-family: /*[[font]]*/;
}

/* Rahmen */
#index_01_p {
  /*[[rahmen]]*/
  border: none;
}

/* Premium Tage */
#index_09>span {
  color: blue !important;
}

/* "Manager-Online" */
#index_10>span {
  color: blue !important;
}

/* "Manager Heute" */
#index_11>span {
  color: blue !important;
}

/* Aktive Links */
.aktiv>a {
  color: blue !important;
}

.auswahl {
  margin-top: 10px !important;
}

/* ---------- Manager-Fenster ---------- */

/* "Manager" */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
  color: black !important;
}

/* Manager Name */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) {
  color: blue !important;
}

/* "Firma */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
  color: black !important;
}

/* Firmen Name */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {
  color: blue !important;
}

/* "Bargeld" */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) {
  color: black !important;
}

/* Kontostand */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(2) {
  color: #195C11 !important;
  font-size: 115%;
  font-variant: small-caps !important;
}

/* "Level" */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) {
  color: black !important;
}

/* Manager Level */
#index_05 > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(2) {
  color: blue !important;
}

