css:
@-moz-document url-prefix("https://feedly.com/"), url-prefix("http://feedly.com/") {
#sideArea,
#mytab,
#librarytab,
#integrationstab_header,
#fixedProfile .button,
#searchPromo,
.fx-button[data-uri="pro/navbar"],
.widget-related-feeds {
	display: none !important;
}
#feedlyTabs #mytab:first-child {
	display: block !important;
}
#savedtab {
	margin-bottom: -20px;
}
#feedlyTabs {
	overflow-y: auto;
	padding-right: 30px;
	width: 220px;
}
#feedlyPart {
	float: none;
	padding: 0;
}
#feedlyPage {
	width: 100% !important;
}
#feedlyPage * {
	max-width: none !important;
}
.entryholder {
	padding-bottom: 0;
}
.entryHeader {
	margin-bottom: 20px;
}
.entryHeader a.title {
	margin-bottom: 5px !important;
}
.content img {
	margin-top: 10px !important;
	margin-bottom: 10px !important;
}
.entryBody > div > img {
	margin-top: 20px !important;
	margin-bottom: 20px !important;
}
.websiteCallForAction {
	margin-top: 20px;
}
.hercule-search {
	height: 21px;
}
.u100Entry {
	margin-top: 1em;
}
.u100Entry .title {
	margin-top: 0.7em;
}
.shareBarHolder,
.headerInfo.headerInfo-article {
	margin-top: 1.5em;
}
}
