css:
@-moz-document url(about:home) {

  html {
    font: message-box;
    font-size: 100%;
    color: #C8C8C8;
    background-color: #252525;
    }
 
  a {
    color: #388FFF;
    text-decoration: none;
    }
  
  #snippets {
    display: inline-block;
    text-align: start;
    margin: 12px 0px;
    color: #C8C8C8;
    font-size: 75%;
    min-height: calc(1.25 * 3em);
    }
  
  #launcher {
    display: -moz-box;
    -moz-box-align: center;
    -moz-box-pack: center;
    width: 100%;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0) inset, 0px -1px 0px #212121;
    border-top: 1px solid rgba(0, 0, 0, 0.03);
    background-color: rgba(0, 0, 0, 0.05);
    }
 
  #restorePreviousSessionSeparator {
    width: 1px;
    height: 70px;
    margin: 0px 10px;
    background-image: linear-gradient(rgba(255, 255, 255, 0), #444, rgba(255, 255, 255, 0)), linear-gradient(rgba(3, 14, 27, 0), rgba(3, 14, 27, 0.2), rgba(3, 14, 27, 0)), linear-gradient(rgba(255, 255, 255, 0), rgba(255, 255, 255, 0.35), rgba(255, 255, 255, 0));
    background-position: left top, center center, right bottom;
    background-size: 1px auto;
    background-repeat: no-repeat;
    }
  
  
    #searchText:focus + #searchSubmit, #searchText + #searchSubmit:hover, #searchText[autofocus] + #searchSubmit {
    border-color: #484848 #2A2A2A #242424;
    color: #FFF;
    }
    
    #searchText {
    -moz-box-flex: 1;
    padding: 6px 8px;
    background: rgba(255, 249, 249, 0.9) none repeat scroll 0% 0% padding-box;
    border-width: 1px;
    border-style: solid;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-color: rgba(23, 51, 78, 0.15) rgba(23, 51, 78, 0.17) rgba(23, 51, 78, 0.2);
    box-shadow: 0px 1px 0px rgba(8, 22, 37, 0.02) inset, 0px 0px 2px rgba(8, 22, 37, 0.1) inset, 0px 1px 0px rgba(255, 255, 255, 0.2);
    border-radius: 2.5px 0px 0px 2.5px;
    color: black;
    }
    
    #topSection {
    text-align: center;
    color: black;
    }

    #searchIcon {
    border: 1px none transparent;
    padding: 0px;
    margin: 0px;
    width: 36px;
    height: 36px;
    background: transparent url("chrome://browser/skin/search-indicator-magnifying-glass.svg") no-repeat scroll center center;
    position: relative;
}
    
}

@-moz-document url(about:newtab) {
 
#newtab-vertical-margin {
    display: -moz-box;
    position: relative;
    -moz-box-flex: 1;
    -moz-box-orient: vertical;
    background-color: #252525;
    }
  
  .newtab-site:hover .newtab-title {
    color: #388FFF;
    }
 #newtab-scrollbox:not([page-disabled]) {
    color: #000;
    }
  
}

@-moz-document url(about:addons) {
  
    *|*:root {
    -moz-appearance: none;
    padding: 18px;
    background-color: #181818;
    background-image: url("chrome://global/skin/inContentUI/background-texture.png");
    color: #313739;
    }
    
    .list {
    -moz-appearance: none;
    margin: 0px;
    border: medium none;
    background-color: #757575;
    color: white;
    }
    
    .view-header {
    padding: 4px;
    margin: 0px;
    min-height: 41px;
    background-color: #4D4D4D;
    border-bottom: 1px solid #424242;
    }
    
    .addon {
    border-bottom: 1px solid #6E6E6E;
    padding: 5px;
    }
    
    .category[selected] {
    background-color: #757575;
    color: #313739;
    border-color: #606060;
    }
    
    *|*.main-content {
    color: #313739;
    background-color: #757575;
    border-color: #606060;
    }
    
    .category {
    -moz-appearance: none;
    border-width: 1px;
    -moz-border-end-width: 0px;
    border-style: solid;
    border-color: transparent;
    padding: 10px 4px;
    -moz-box-align: center;
    overflow: hidden;
    min-height: 0px;
    color: #CCC;
    }
    
    .addon-view[notification="error"] {
    background-image: url("chrome://mozapps/skin/extensions/stripes-error.png"), linear-gradient(#560000, rgba(2, 0, 0, 0.74));
    background-repeat: repeat-x;
    }
    
    .detail-view-container {
    font-size: 110%;
    color: #DBDBDB;
    }
    
    .button-link {
    -moz-appearance: none;
    background: transparent none repeat scroll 0% 0%;
    border: medium none;
    text-decoration: underline;
    color: rgba(255, 255, 255, 0.9);
    cursor: pointer;
    min-width: 0px;
    margin: 0px 6px;
    }
        
}

@-moz-document 
    url(about:preferences),
    url(about:preferences#general),
    url(about:preferences#search),
    url(about:preferences#content),
    url(about:preferences#applications),
    url(about:preferences#privacy),
    url(about:preferences#security),
    url(about:preferences#sync),
    url(about:preferences#advanced),
    url(about:preferences*)
    {
    page {
        filter: invert(80%) hue-rotate(180deg);
        background-color: #252525;
    }
    
}


@-moz-document url(about:robots) {
    html {
    background: #252525 none repeat scroll 0% 0%;
    }

    #errorPageContainer {
    position: relative;
    min-width: 13em;
    max-width: 52em;
    margin: 4em auto;
    border: 1px solid #C2C2C2;
    border-radius: 10px;
    padding: 3em;
    -moz-padding-start: 30px;
    background: #878787 url("moz-icon://stock/gtk-dialog-warning?size=dialog") no-repeat scroll left 0px content-box border-box;
}
    
}
