/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*----- SITES TO EXCLUDE -----*/
@-moz-document regexp("https?://(?!(localhost|.*.4chan.org|.*.inoreader.com)).*"),

    
    
 url-prefix(ftp://), url-prefix(about), url-prefix(javascript) {
  
     html,body 
     {
         background: #37393D !important; 
         color:white !important;
     }

    div, table, span, label, tr, td, p, article, h1, h2, h3, h4, h5, h6, section, ul, li , ol, font, header, footer
    { 
        background: #37393D !important;
        color: lightgray !important;
    }

     code
     {
        background: #37393D !important;
        color: lightyellow !important;
         
     }
     
     
    *:before, *:after
     {
         background-color: transparent !important; 
     }
    
    a, a *, a:link, a:link *
    {
        color: #38B1F7 !important;
        text-decoration: none !important;
        background-color: #37393D !important;
    }
        
    a:visited, a:visited * 
    {
        color: #CE12C5 !important; 
        text-decoration: none !important;
    }
    
    
    a:hover, a:hover *, a:active, a:active * 
    {
        color: #CE12C5 !important; 
        text-decoration: underline !important;
    }
    
    input[type="button"], input[type="submit"], button
    {
        -moz-appearance: none !important;
        background-color: orange !important;
        border: 0 !important;
        color:whitesmoke !important;
        font-weight: bold !important;      
    }
         
}

/* ----- ABOUT PAGES BACKGROUND ----- */
@-moz-document url-prefix(about) {
    * { background: none var(--color-1) !important; }
    body > * { background-color: var(--color-1) !important; }
    #list-view, #list-view * { background-color: var(--color-1) !important; }
    #detail-view, #detail-view * { background: var(--color-1) !important; }
    #addons-page > * >  *, #addons-page > * > * > * > * { border-color: transparent !important; }
}
