css:
@-moz-document url-prefix("http://reclaimerhub.com/"), url-prefix("http://austin-ford.squarespace.com") {

.show-on-scroll-wrapper #header {
    background-color: rgba(8,61,166,0.3) !important;
}

#siteWrapper {
    background-color: #0D1524 !important;
    color: #687CA3 !important;
}

#sidecarNav {
    background-color: #0D1524 !important;
}

*::selection {
    background-color: rgba(0,21,255,0.36) !important;
    color: #cecece !important;
}

.sqs-widgets-audio-player.dark {
    background-color: rgba(8,61,166,0.36) !important;
}

.small-button-style-outline .sqs-block-button .sqs-block-button-element--small, .medium-button-style-outline .sqs-block-button .sqs-block-button-element--medium, .large-button-style-outline .sqs-block-button .sqs-block-button-element--large {
    color: rgba(8,61,166,1.0) !important;
    border-color: rgba(8,61,166,0.64) !important;
}

.small-button-style-outline .sqs-block-button .sqs-block-button-element--small:hover, .medium-button-style-outline .sqs-block-button .sqs-block-button-element--medium:hover, .large-button-style-outline .sqs-block-button .sqs-block-button-element--large:hover {
    color: #9FCF27 !important;
    background-color: rgba(8,61,166,0.19) !important;
    border-color: rgba(8,61,166,0.81) !important;
}

.has-promoted-gallery #promotedGalleryWrapper .sqs-gallery-block-slideshow .meta .meta-description p:last-child>a, .has-promoted-gallery .promoted-gallery-wrapper .sqs-gallery-block-slideshow .meta .meta-description p:last-child>a {
    background-color: rgba(0,21,255,0.36) !important;
}

.has-promoted-gallery #promotedGalleryWrapper .sqs-gallery-block-slideshow .meta .meta-description p:last-child>a:hover, .has-promoted-gallery .promoted-gallery-wrapper .sqs-gallery-block-slideshow .meta .meta-description p:last-child>a:hover {
    background-color: rgba(0,21,255,0.81) !important;
}

.enable-nav-button #headerNav nav>div:not(.folder):last-child a:hover, .enable-nav-button #sidecarNav nav>div:not(.folder):last-child a:hover {
	color: #9FCF27 !important;
    background-color: rgba(8,61,166,1.0) !important;
}
    
#headerNav nav a:hover, #sidecarNav nav a:hover, #headerNav nav label:hover, #sidecarNav nav label:hover {
    color: #9FCF27 !important;
    background-color: rgba(8,61,166,1.0) !important;
}

body:not(.has-banner-image).transparent-header #header, .collection-type-gallery.has-banner-image.transparent-header #header {
    background-color: rgba(8,61,166,0.3) !important;
}

#headerNav nav .subnav, #sidecarNav nav .subnav {
    background-color: rgba(8,61,166,0.3) !important;
}

.category-nav .nav-section-label, .folder-nav .nav-section-label {
    color: #A7B4CF !important;
}
    
.category-nav a, .folder-nav a, .category-nav a:visited, .folder-nav a:visited {
    color: #687CA3 !important;
}

.category-nav a:hover, .folder-nav a:hover, .category-nav a:visited:hover, .folder-nav a:visited:hover {
    color: #9FCF27 !important;
}

h1, article header h1 a, .pre-footer-inner a:not(.sqs-block-button-element):not(.sqs-svg-icon--wrapper), .pre-footer-inner a:visited:not(.sqs-block-button-element):not(.sqs-svg-icon--wrapper) {
    color: #0D5EFF !important;
    border-bottom: 1px solid rgba(100, 100, 100, 0.0) !important;
}

h2 {
    color: #1F4BA3 !important;
}

h3 {
    color: #A7B4CF !important;
}

#preFooter {
    background-color: #10141C !important;
}

#footer {
    background-color: #0B0D12 !important;
}

#footer nav:not(.social-account-list) a, #footer nav:not(.social-account-list) a:visited, #footer nav:not(.social-account-list) label {
    color: #cecece !important;
}

.html-block a:not(.sqs-block-button-element), .markdown-block a:not(.sqs-block-button-element), .entry-more-link a:not(.sqs-block-button-element), .twitter-block a:not(.sqs-block-button-element), .foursquare-block a:not(.sqs-block-button-element), .rss-block a:not(.sqs-block-button-element), .layout-caption-below .image-caption a:not(.sqs-block-button-element), .summary-excerpt a:not(.sqs-block-button-element), .album-description a:not(.sqs-block-button-element), .product-excerpt a:not(.sqs-block-button-element), .product-description a:not(.sqs-block-button-element), .eventlist-excerpt a:not(.sqs-block-button-element), .html-block a:not(.sqs-block-button-element):visited, .markdown-block a:not(.sqs-block-button-element):visited, .entry-more-link a:not(.sqs-block-button-element):visited, .twitter-block a:not(.sqs-block-button-element):visited, .foursquare-block a:not(.sqs-block-button-element):visited, .rss-block a:not(.sqs-block-button-element):visited, .layout-caption-below .image-caption a:not(.sqs-block-button-element):visited, .summary-excerpt a:not(.sqs-block-button-element):visited, .album-description a:not(.sqs-block-button-element):visited, .product-excerpt a:not(.sqs-block-button-element):visited, .product-description a:not(.sqs-block-button-element):visited, .eventlist-excerpt a:not(.sqs-block-button-element):visited {
    color: #9FCF27 !important;
}

.pagination>div a {
    color: #9FCF27 !important;
}

.sqs-block-horizontalrule hr {
    background-color: rgba(159,207,39,0.36) !important;
}

.form-wrapper .field-list .field .field-element {
    background: #141414 !important;
    border: 1px solid #9FCF27 !important;
}

textarea {
    color: #cecece !important;
    background-color: #262626 !important;
    border: #cecece !important;
}

.squarespace-comments .comment-btn-wrapper {
background-color: #141414 !important;
border: 1px #141414 !important;
border-color: #141414 !important;
}

.squarespace-comments .new-comment-area .input {
    border: 1px solid #9FCF27 !important;
}
}
