css:
@-moz-document domain("privatelee.com") {
body, input[type=text], .box
{
    color: #909090 !important;
    background-color: #000000 !important;
}
.ac_results, .ac_even, .ac_odd {
    color: #008800 !important;
    background-color: #222222 !important;
    border: 1px solid #003300;
}
input[type=text] { color: #8aa88a !important; }
a, a:active, a:visited
{
    font-weight: bold !important;
    text-decoration: none !important;
    color: #006600 !important;
}
a:hover
{
    text-decoration: underline !important;
}
#dI, #dI > h3 + span + span + br + div
{
    border-left: 2px solid #006600 !important;
    border-right: none !important;
    border-top: none !important;
    border-bottom: none !important;
    position: relative !important;
    left:10px !important;
    padding-left: 10px !important;
}
#dI > h3 + span + span + br + div { margin-top: 6px !important; }
#tblList
{
    border: 1px solid #006600 !important;
    border-collapse: collapse !important;
}
#tblList tr
{
    background-color: #000000 !important;
    border: none !important;
}
#tblList td
{
    border-top: none !important;
    border-bottom: none !important;
    border-left: 1px solid #006600 !important;
    border-right: 1px solid #006600 !important;
}
#tblList td[style*="width: 100px; background-color: rgb(255, 255, 255);"] { background-color: #000000 !important; }
input[type=button] { margin-left: 5px !important; }
#dCap span, #dCap
{
    border: none !important;
    color: #aaaaaa !important;
    background-color: #333333 !important;
    padding-right: 0 !important;
    padding-left: 0 !important;
    padding-bottom: 4px !important;
    padding-top: 0 !important;
}
#dCap span { width: 100% !important; }
}
