#TabsToolbar tab.tabbrowser-tab {
    margin-left: 5px!important; 
    margin-right: 5px!important;
    /* left: 15px; */
    position: relative;
}

#TabsToolbar .tab-background {
   /* background: transparent !important;
    background: #fff!important; */
}
#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
   background: transparent !important;
    border: none!important;
    background-image: none!important;
}
#TabsToolbar .tab-background-middle {
    background: transparent !important;
    border: none!important;
    background-image: none!important;
}

#TabsToolbar .tab-background-start::before,  #TabsToolbar .tab-background-end::before {
  background: transparent !important;
    border: none!important;
    background-image: none!important;
}

/* removes seperators between pinned tabs */
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    background: transparent !important;
    border: none!important;
    background-image: none!important;
}

#TabsToolbar .tabbrowser-tab:after
{
   background: transparent !important;
    border: none!important;
    background-image: none!important;
}

#tabbrowser-tabs::before, #tabbrowser-tabs::after
{
     background: transparent !important;
    border: none!important;
    background-image: none!important;
}

.tab-background { opacity: 0;}

.tab-background[selected=true] {
    background-color: transparent;
    background-image: none!important;
    opacity: 5.0;
}

.tab-background[selected=true] hbox:before, .tab-background[selected=true] hbox:after { background: transparent; background-image: none!important; list-style-image: unset; list-style-type: unset; }

.tab-background[selected=true] {
    opacity: 0.5;
    background: rgba(0, 50, 220, 0.5);
    filter: brightness(50);
}

/* center text */
.tab-text {
    text-align: center !important;
    font-family: 'Helvetica', 'Arial'!important;
    font-size: 11px;
    color: rgba(200, 200, 200, 0.8);
    text-shadow: initial!important;
}

.tab-text[selected=true]
{
