css:
@-moz-document url-prefix("http://jira"), url-prefix("https://jira"), domain("jira.atlassian.com") {
.agile-icon-work {
  background: url("data:image/svg+xml;base64,");
}

.agile-icon-report {
  background: url("data:image/svg+xml;base64,");
}

.agile-icon-plan {
  background: url("data:image/svg+xml;base64,");
}

.icon-sidebar-release {
  background: url("data:image/svg+xml;base64,");
}

.icon-sidebar-components {
  background: url("data:image/svg+xml;base64,");
}

.icon-sidebar-issues {
  background: url("data:image/svg+xml;base64,");
}

button,
button *,
input,
input *,
textarea,
textarea *,
form.aui option,
.toolbar-trigger.js-default-dropdown,
.editable-field:hover,
.editable-field:hover *,
.toolbar-group.pluggable-ops,
.toolbar-group.pluggable-ops *,
.bf-nav-filter-container,
.bf-nav-filter-container *,
.aui-button {
  background: #2b4d65 !important;
  color: #ededed !important;
}

.jira-dialog-heading,
.jira-dialog-heading *,
.buttons-container .buttons-container * .jira-dialog-content,
.jira-dialog .jira-dialog-content,
.jira-dialog .buttons-container {
  background: #1a1a1a !important;
  color: #ededed !important;
}

.atlassian-autocomplete .suggestions li {
  color: #ededed;
}

.issue-link-summary {
  color: #325b77 !important;
}

.pagination-view,
.issue-list > li {
  background: #333333 !important;
}

#syntaxplugin span[style*=black] {
  color: #FFFFFF !important;
}
#syntaxplugin span[style*=blue] {
  color: #42769a !important;
}

.sla-tag.sla-tag-breached,
code {
  color: #000000;
}

.ghx-swimlane-header:after,
.ghx-heading:after,
.ghx-issue .ghx-end,
.ghx-swimlane-header:hover,
.ghx-heading:hover,
.ghx-issue .ghx-end:hover,
#ghx-pool > div.ghx-swimlane > ul > li > div:hover .ghx-end,
.ghx-issue,
.ghx-issue.ghx-selected .ghx-end {
  background-color: transparent;
  box-shadow: none;
}

.ghx-column.ui-sortable {
  background: #4d4d4d !important;
}

#ghx-pool {
  background: inherit;
}

#ghx-column-headers .ghx-column,
#ghx-column-headers .ghx-column * {
  background: #4d4d4d !important;
  border: none;
  color: #ededed;
}

#ghx-header {
  margin: 0;
}

.ghx-column-headers .ghx-column,
.ghx-columns .ghx-column {
  background: #4d4d4d;
}

.ghx-flagged * {
  background-color: #8b0000;
}

.ghx-columns .ghx-busted,
.ghx-columns .ghx-busted-max {
  background: #8b0000 !important;
}

.ghx-column h2 {
  color: #ededed !important;
}

.ghx-backlog-header,
.ghx-end {
  background: #333333 !important;
  color: #ededed !important;
}

.aui-badge {
  background: #ededed !important;
  color: #333333 !important;
}

.ghx-everything-else {
  background: #333333 !important;
}

.wiki-edit-toolbar {
  background: #333333;
}

#issue-tabs > li {
  background: #2b4d65 !important;
  color: #ededed;
}
#issue-tabs > li > a {
  background: #2b4d65 !important;
  color: #ededed;
}
#issue-tabs > li.active > strong {
  background: #325b77 !important;
  color: #ededed;
}

.ops li,
.ops li * {
  background: #2b4d65 !important;
  color: #ededed;
}

.aui-list,
.aui-list * {
  color: #ededed !important;
}

.dropdown-text {
  background: #2b4d65 !important;
}

html,
body header nav.aui-header,
body header nav.aui-header *:not(.jira-feedback-plugin-icon),
body header.aui-page-panel-nav {
  background: #000000;
}

.ghx-header *,
.issue-header-content,
.aui-page-header *,
.command-bar,
.toggle-title,
.saved-search-selector,
.saved-search-selector *,
.navigator-search,
.aui-dropdown2,
.aui-page-panel-nav {
  background: #333333 !important;
  color: #ededed !important;
}

.aui-sidebar-wrapper,
#content .aui-page-panel-nav .tabs.vertical strong {
  background: #333333 !important;
  color: #42769a !important;
}

#content .aui-page-panel-nav .tabs.vertical a:focus,
#content .aui-page-panel-nav .tabs.vertical a:hover,
#content .aui-page-panel-nav .tabs.vertical a:focus *,
#content .aui-page-panel-nav .tabs.vertical a:hover * {
  background: #4d4d4d !important;
}

.aui-navgroup-horizontal .aui-nav > .aui-nav-selected a,
.aui-navgroup-vertical .aui-nav .aui-nav-selected a,
.aui-nav-vertical .aui-nav .aui-nav-selected a,
.aui-nav-selected > a {
  color: #42769a !important;
}

h1,
h2,
h3,
h4 {
  color: #325b77 !important;
}

.user-content-block h1,
.user-content-block h2,
.user-content-block h3,
.user-content-block h4,
.user-content-block h5,
.user-content-block h6,
.user-content-block:hover h1,
.user-content-block:hover h2,
.user-content-block:hover h3,
.user-content-block:hover h4,
.user-content-block:hover h5,
.user-content-block:hover h6 {
  color: #ffe4c4 !important;
}

#footer,
#footer * {
  background: #000000 !important;
}

.aui-message.hint,
.aui-message-hint,
.aui-message.info,
.aui-message-info,
.aui-message,
.aui-message.hint *,
.aui-message-hint *,
.aui-message.info *,
.aui-message-info *,
.aui-message * {
  background: #4d4d4d !important;
}

.aui-dialog2-header,
.aui-dialog2-footer,
.aui-dialog2-header *,
.aui-dialog2-footer * {
  background: #4d4d4d !important;
  color: #ededed !important;
}

.aui-message {
  color: #ededed !important;
}

.layout .column .placeholder,
.layout .column.empty .empty-text {
  background: #4d4d4d !important;
}

div#slider-wrapper {
  background: #4d4d4d !important;
}

.content-container,
.bf-gs-content,
.bf-gs-section,
* {
  background: #333333;
  color: #ededed;
  text-shadow: none !important;
  box-shadow: none !important;
}

.aui-dropdown2:hover,
.aui-dropdown2.aui-style-default a {
  background: #333333 !important;
  color: #ededed !important;
}

.aui-dropdown2 li:hover,
.aui-dropdown2 li *:hover {
  background: #4d4d4d !important;
}

a {
  color: #5985c6;
}

}
