css:
@-moz-document domain("reddit.com") {
/* All */
.md .expando-button {
	border-radius:3px;
	height: 12px!important;
}

/* Video */
.md .expando-button.video { background-position-y: -404px; }
.md .expando-button.video-muted {
	background-position-y: -100px;
	border-top: #ccccc9 1px solid;
}

.md .expando-button.video:hover { background-position-y: -376px!important; border-top: #466599 1px solid;}
.md .expando-button.video-muted:hover {
	background-position-y: -124px!important;
	border-top-color: #466599;
}

/* Images */
.md .expando-button.image {
	background-position: 0px -3px!important;
}
.md .expando-button.image:hover { background-position: 0px -27px!important; }

/* Text */ 
.md .expando-button.selftext { background-position-y: -464px; }
.md .expando-button.selftext:hover { background-position-y: -435px; }

}
