css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com") {
.ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard{display:none !important}

#header {
        background-color: #00A2FF;
        box-shadow: 0 0 60px rgba(25,25,25,0.5);
    }
    
.navigation .notification-icon {
        background: #b71c1c;
    }

.forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: #b71c1c !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button {
  		background-color: #353535 !important;
        border-color: transparent;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: #00A2FF!important;
        border-color: transparent;
    }

.rbx-header .logo {
  background-repeat: no-repeat;
  background-size: auto auto;
  width: 0px;
  height: 30px;
  display: inline-block;
}

.rbx-nav-collapse .rbx-nav-notification {
  border-radius: 8px;
  color: white;
  left: 0px;
  top: 6px;
  width: 22px;
  height: 16px;
  position: absolute;
  text-align: center;
}

.page-content {
  float: none;
  margin: 0 auto;
  margin-top: -100px;
  margin-right: auto;
  margin-bottom: 0px;
  margin-left: auto;
  padding: 0 12px;
}


.game-main-content .game-play-buttons .rbx-btn-primary-lg {
  width: 70%;
  background-color: #02b757;
  border-color: #02b757;
  color: white;
  height: auto;
  transition: all,0.5s,ease-in-out;
  padding: 19px 32px;
  font-size: 1.275em;
  line-height: 100%;
  border-radius: 70px;
  position:relative;
  left:0%;
  top:0%;
}

.game-main-content .game-play-buttons .rbx-btn-primary-lg:hover {
  width: 100%;
  background-color: #02b757;
  border-color: #02b757;
  color: white;
  height: auto;
  transition: all,0.2s,ease-in-out;
  padding: 19px 32px;
  font-size: 1.275em;
  line-height: 100%;
  border-radius: 70px;
  position:relative;
  left:0%;
  top:0%;
}

.VisitButton .tooltip {
  display: inline-block;
  font-size: 145%;
  opacity: 1;
  position: relative;
  z-index: 0;
  border-radius: 70px;
  border-color: transparent;
  background-color: transparent;
}

.btn-large, .btn-medium, .btn-small {
  margin: 0;
  display: inline-block;
  zoom: 1;
  text-align: center;
  font-weight: normal;
  text-decoration: none;
  border-width: 0px;
  border-color: transparent;
  cursor: pointer;
}

.scroller {
  height: 221px;
  width: 50px;
  background-color: white;
  background-color: rgba(255,255,255,1);
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  zoom: 1;
  position: absolute;
  z-index: 950;
  border-radius: 0 3px 3px 0;
  box-shadow: 0 6px 6px 0 #ccc;
}

.scroller:hover {
    zoom: 1;
    cursor: pointer;
    background-color: rgba(225,225,225,1);
    box-shadow: 0 6px 6px 0 #ccc;
}

.scroller.next .arrow {
  right:18px;
}

.scroller.prev .arrow span {
  left:10px;
}

#TopAbpContainer {
  		display: none;
    }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left {
  		display: none;
    }

#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
		background: linear-gradient(to bottom,#e53935,#b71c1c);
    }
#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4) {
  		display: none;
    }
body {
  	font-family: Segoe UI;
    }
#GamesPageLeftColumn { width: auto; margin-right: 10px !important; margin-left: 45px !important; }
#GamesListsContainer > div {
	background-color: transparent !important;
	width: 95% !important;
    }
}
