css:
@-moz-document domain("slaent.com") {
a {
  color: #da4;
}

section.home-forums {
  background-color: #1f1f1f;
}

section.threads {
  background-color: #2f2f2f;
}

#user-cp ul.ui-inputs li .ui-user-avatar .input span.helptext {
  border-top: 1px solid #eee;
  text-align: left;
  margin-top: 1.5em;
  padding-top: 1.5em;
  padding-bottom: 0em;
  margin-bottom: 0em;
  line-height: 1em;
}

div.thread.js-post-form {
    background-color: #00ff00;
}

#user-cp input.ui-input, #user-cp textarea.ui-input {
  border-color: #333;
}


#user-cp h2 {
  border-bottom: 1px solid #333;
  margin-top: 0em;
  padding-top: 0em;
  padding-bottom: 0.75em;
  margin-bottom: 0.75em;
  color: #fff;
}

#user-cp ul.ui-inputs li {
  border-bottom: 1px solid #111;
  overflow: hidden;
}

.thread__topic-body {
  background: #fff;
  border-bottom: 1px solid #f00;
  position: relative;
  margin-top: 0em;
  padding-top: 3em;
  padding-bottom: 3em;
  margin-bottom: 0em;
}
html.wf-proximanova-i4-active.wf-proximanova-i7-active.wf-proximanova-n1-active.wf-proximanova-n3-active.wf-proximanova-n4-active.wf-proximanova-n7-active.wf-active {
    background-color: #2f2f2f;
}


h3.thread__title.thread__title--forum a {
    color: #e0e0e0;
}

section.welcome {
    background-color: #1f1f1f;
}

h2.forum__title a {
    color: #f0f0f0;
}

h3.forum__description {
    color: #757575;
}

#footer {
    background-color: #2f2f2f;
}

section.thread__replies {
    background-color: #2f2f2f;
}

div.post__body {
    color: #e0e0e0;
    font-style: normal;
    font-weight: 100;
    font-size: 13px;
    font-family: Helvetica;
    line-height: 140%;
}

span.spoiler {
	background-color: #aaa;
    color: #aaa;
    border-radius: 0;
}

a.post__userlink {
    color: #d0a040;
   
    font-weight: normal;
    font-size: 13px;
}

span.post__usertag {
    color: #909090;
}

div.post__body blockquote {
    background-color: #1f1f1f;
	color: #e0e0e0;
}

section.thread__options {
    background-color: #2f2f2f;
}

div.thread.js-thread {
    background-color: #2f2f2f;
}

button.post-form__icon.js-bbcode {
    color: #757575;
}

h5.post__date.js-to-local-date {
    color: #909090;
}

h4.post__username {
    color: #757575;
}

div.post__body a {
    font-weight: normal;
    text-decoration: none;
    text-transform: none;
    font-variant: normal;
    font-style: normal;
    color: #d0a040;
}



a.navigation__icon.navigation__icon--user {
    background-color: #303030;
}

a.post__icon.post__icon--permalink {
    color: #d0a040;
}

a.post__icon.post__icon--quote.js-quote-post {
    color: #909090;
}

div.post__body.thread__topic-body {
    background-color: #3f3f3f;
    border-color: #906000;
}

div.forums__container {
    background-color: #1f1f1f;
}

div.navigation__container {
    background-color: #1f1f1f;
}

div.ui-user-cp {
    background-color: #101010;
}

div.ui-user-cp form h2 {
    color: #757575;
}

label.ui-label {
    color: #757575;
}

input.ui-input {
    color: #757575;
}

span.helptext {
    color: #757575;
}

input.ui-button {
    color: #757575;
}

div.form h2 {
    color: #757575;
}

ul.ui-inputs li {
    color: #757575;
}

.navigation__icon:before {
  background: #1f1f1f;
}

.ui-button, .button {
    border-color: #757575;
}

.ui-button:hover,.button:hover {
    background: #000000;
}

.navigation__search-field--btn:before {
    color: #00ff00;
}

a.thread__last-post-icon {
    color: #757575;
}

h2.threads__title {
    color: #757575;
}

h4.thread__last-post-username {
    color: #757575;
}

h4.thread__last-post-date.js-to-local-date {
    color: #757575;
}

h4.thread__owner-meta {
    color: #757575;
}

li.page-nav__item.page-nav__item--num {
    color: #757575;
}

button.button.post-form__button.post-form__button--submit.js-post-submit-button {
    color: #575757;
}

section.forum__options {
    color: #757575;
    background-color: #e0e0e0;
}

a.button {
    color: #757575;
}

h2.forum__header-title {
    color: #757575;
}

textarea.post-form__body.is--clean.js-post-form-body--bbcode {
    color: #757575;
}

h2.welcome__title {
    color: #d0a040;
}

a.ui-button.welcome__button {
    color: #757575;
}

strong.name {
    color: #757575;
}

blockquote {
	background: #000;
	border: 3px solid #000000; /* Leave */
	border-width: 0.0625em;
	border-style: solid;
	padding: 0.6875em;
	border-width: 0 0 0 3px;
}



.post__avatar-wrapper {
  background: #d0a040;
  border: #d0a040;
  margin: 0 auto;
  position: relative;
  z-index: 1;
  -moz-border-radius: 50%;
  -webkit-border-radius: 50%;
  border-radius: 50%;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  border-width: 0.0625em;
  border-style: solid;
  padding: 0.3125em;
  height: 8em;
  width: 8em;
}
.post__avatar:before {
  background: #906000;
}    

.navigation__search-field {
  background: #1f1f1f;
  float: left;
  font-size: 0.75em;
  line-height: 1.5em;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  border-width: 0em;
  border-style: solid;
  padding: 0em;
  margin-top: 0em;
  padding-top: 0.375em;
  padding-bottom: 0.375em;
  margin-bottom: 0em;
  color: #757575;
}

.col--welcome {
  border-right: 1px solid #1f1f1f;
}

.thread__item {
  color: #222;
  border-top: 1px solid #1f1f1f;
}

.thread__owner-avatar, .thread__last-post-avatar {
  overflow: hidden;
  -moz-border-radius: 50%;
  -webkit-border-radius: 50%;
  border-radius: 50%;
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  border-width: 0em;
  border-style: solid;
  padding: 0.1875em;
  height: 3em;
  width: 3em;
  border: 0px;
}

.post+.post .post__meta {
  border-top: 0px;
}

.navigation__search-field--btn:before {
  color: #575757;
}

.forum__options:first-of-type .forum__options-wrapper {
  border-bottom: 1px solid #000;
}

.forum__options:last-of-type .forum__options-wrapper {
  border-top: 1px solid #0f0;
}

.thread__options-wrapper {
  border-top: 1px solid #111;
  margin: 0 auto;
  max-width: 60em;
  margin-top: 0em;
  padding-top: 0.75em;
  padding-bottom: 0.75em;
  margin-bottom: 1.5em;
}

div.post-form__format {
    border-style: none;
}
}
