css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sims-prd.sfu.ca"), domain("go.sfu.ca") {
  
  /* Site Header */
  .header {
    background: transparent -moz-linear-gradient(left center , #1A0909 0%, #602A2A 100%) repeat scroll 0% 0% !important;
  }
  
  /* Site Header Title */
  .site-title {
    color: #9E8974;
  }
  
  /* Page background */
  .PSPAGE {
    background: #222 !important;
    color: #eee !important;
  }
  
  /* Text */
  .PAPAGEINSTRUCTIONS,
  .PABOLDTEXT {
    color: #ccc !important;
    background-color: #333 !important;
  }

  /* Menu bar */
  #pthnavcontainer {
    filter: invert(0.93) !important;
    -webkit-filter: invert(0.93) !important;
  }
  
  /* Labels */
  .PSDROPDOWNLABEL, .PSEDITBOXLABEL, .PSRADIOBUTTON {
    color: #457BE7 !important;
  }
  
  /* Inner container background */
  .PSPAGECONTAINER, .PSPROCESSING, .PTNAVSELCHILDEDGELT, .PTNAVSELPARENTBK {
    background: #333 !important;
    color: #ccc !important;
  }
  
  /* Group boxes */
  .SSSGROUPBOXLEFT,
  .PSGROUPBOX {
    background: #333 !important;
    color: #ccc !important;
  }

  /* Group boxes */
  .SSSGROUPBOXDKBLUE {
    color: rgb(74,89,140) !important;
    background-color: #0C0C0C important;
  }  

  /* Group box (Class search) */
  .PAGROUPBOXLABELLEVEL1 {
    background-color: rgb(32, 44, 86) !important;
    color: #ddd !important;
  }

  /* Grid > Odd */
  .PSLEVEL1GRIDODDROW {
    background: #333 !important;
    color: #ccc !important;
  }
  
  /* Grid > Even */
  .PSLEVEL1GRIDEVENROW {
    background: #333 !important;
    color: #ccc !important;
  }
  
  /* Grid header */
  .PSLEVEL1GRIDCOLUMNHDR {
    background-color: rgb(9, 16, 27) !important;
    border-top: rgb(41, 46, 54) !important;
    border-bottom: rgb(41, 46, 54) !important;
    border-right: rgb(41, 46, 54) !important;
  }

  /* Grid header text */
  .PSLEVEL1GRIDCOLUMNHDRTXT, .PSLEVEL1GRIDCOLUMNHDR:link, .PSLEVEL1GRIDCOLUMNHDR:visited, .PSLEVEL1GRIDCOLUMNHDRDISABLED, .PSLEVEL4GRIDNAVIGATIONBAR:link, .PSLEVEL4GRIDNAVIGATIONBAR:active, .PSLEVEL4GRIDNAVIGATIONBAR:visited {
    color: rgb(121, 131, 168) !important;
  }

  /* Grid contents */
  .PSLEVEL2GRIDROW,
  .PSEDITBOX_DISPONLY, .PSLONGEDITBOXDISABLED,
  .PAADDITIONALINSTRUCTIONS, .PSDROPDOWNLIST, .PSHYPERLINKDISABLED, .PSLONGEDITBOX, .PSHYPERLINKDISABLED, .PSHYPERLINKNOULDISABLED, .PSHYPERLINKNOULBOLDDISABLED, .PSHYPERLINKNOULBOLDHOVERDISABLED, .PSHYPERLINKNOULHOVERDISABLED, .PSSRCHRESULTSEVENROWDISABLED, .PSSRCHRESULTSODDROWDISABLED {
    background: #333 !important;
    color: #ccc !important;
  }

  /* Other grids (Class search) */
  .PSLEVEL3GRIDROW {
    background: #222 !important;
    color: #ccc !important;
  }
  
  /* Site Header Links */
  .global-links li a {
    color: #C0AF9E !important;
  }
  
  /* Links */
  .PSHYPERLINK, .PSHYPERLINK:link, .PSHYPERLINK:visited, .PSSRCHRESULTSEVENROW:link, .PSSRCHRESULTSEVENROW:visited, .PSSRCHRESULTSODDROW:link, .PSSRCHRESULTSODDROW:visited {
    color: #9494F9 !important;
  }
  
  .SSSFOOTERLINKBOLD, .SSSFOOTERLINK {
    color: #496EBD !important;
  }
  
}
