css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com") {
#header {
        background-color: #07d500;
    }
    
.navigation .notification-icon {
        background: #07d500;
    }

.forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: #07d500 !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button {
  		background-color: #07d500 !important;
   		border-color: #07d500;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: #07d500 !important;
        border-color: #07d500;
    }

#TopAbpContainer {
  		display: none;
    }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left {
  		display: none;
    }

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
        background: url(http://i.imgur.com/2zfLaSK.png) top center repeat-x #DDDDDB;
    }
    
#navbar-search-input {
		border: 1px solid #07d500;
    }
#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
		background: linear-gradient(to bottom,#07d500,#07d500);
    }
#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4) {
  		display: none;
    }
body {
  	font-family: Segoe UI;
    }
#GamesPageLeftColumn { width: auto; margin-right: 10px !important; margin-left: 10px !important; }
#GamesListsContainer > div {
	background-color: transparent !important;
	width: 100% !important;
    }
}
