css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zv.fm") {
  
    body, html{background: linear-gradient(to bottom  , rgba(0, 191, 255, 0.01) 23%, rgba(0, 0, 0,  0.45) 100%)!important;
  background-attachment: fixed !important;background-color: #000 !important;	background-clip: border-box !important;
	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}
     *{font-family:Constantia!important;color: #3ce !important; text-shadow:none!important}          
  
  #header {background: rgba(0, 0, 0, 1)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important}  
  
  /* search & textarea*/  
  .search input.text, input.text, textarea, select, .search .btn2, .btn:hover, .comm_item {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;transition:all 1s ease-in-out !important}
  .search input.text:hover, input.text:hover, textarea:hover, select:hover, .search .btn2:hover{background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;transition:all 1s ease-in-out !important}    
  .search_borders {background:transparent!important}
  .btn {background-color: rgba(0, 0, 0, 0.5)!important;box-shadow: 0px 0px 1px rgba(0, 0, 0, .99 )inset !important;background-image:none!important;border:none!important}  
.user {padding: 5px !important}
  
  /* box */  
  .whb_crn_lt, .whb_crn_rt, .whb_crn_lb, .whb_crn_rb, .whb_gr_t, .whb_gr_b, .whb_gr_l, .whb_gr_r {background:transparent!important;border:none!important}  
  .whb_box {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border-radius:10px!important;padding:20px!important}
  .grey_box {background: rgba(0, 11, 255, 0.06)!important}
    .btn3, .btn4, .btn5, .btn-grey, .song-wrap-xl:hover, .song-wrap:hover, .btn-xlarge{background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.2)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.2)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.2)!important;transition:all 350ms ease-in-out !important;
    background-image:none!important;border:none!important}
 .btn3:active, .btn4:active, .btn-grey:active, .btn-grey.active, .btn3:hover, .btn4:hover, .btn5:hover, .btn-grey:hover, .btn-xlarge:hover {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,11,255,0.1)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,11,255,0.1)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,11,255,0.1)!important;transition:all 350ms ease-in-out !important; background-image:none!important;border:none!important}
  .songs-list-item{border:none!important}
   .prev-btn, .prev-btn span, .prev-btn b, .next-btn, .next-btn span, .next-btn b, .paging .list b, .paging .list a.selected, .paging .list a:hover{background: rgba(0, 0, 0, 0.5)!important;border:none!important}     
  
  /* footer */
  #footer{background:transparent!important}
    
    /* player */  
  #player {background: rgba(0, 0, 0, 0.95)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border-radius:10px!important}
  .player-time-total {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 3%, rgba(0, 11, 191,  0.2) 100%)!important;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, .9 )inset!important;border-color:#047!important}
  .player-time-current {background: linear-gradient(to right , rgba(0, 0, 0, 0.95) 15%, rgba(0, 111, 255,  0.42) 100%)!important;border:none!important;box-shadow: 0px 0px 10px rgba(0, 0, 0, .9 )inset!important;border-radius:6px 15px 15px 6px}
  .player-time-loaded {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 3%, rgba(0, 0, 0,  0.42) 100%)!important}
  .player-corner-left, .player-corner-right {background:transparent!important}
  .player-volume-total {background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 3%, rgba(0, 11, 191,  0.2) 100%)!important}
  .player-volume-current {background: linear-gradient(to top , rgba(0, 0, 0, 0.95) 3%, rgba(0, 0, 0,  0.82) 100%)!important;
      background-image:none!important;border:none!important;box-shadow: 0px 0px 10px rgba(0, 0, 0, .99 )inset!important;}  
     .player-nav-next {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/player/next_zpsjjydcpui.png)!important; background-size: contain !important;background-position: center center !important;background-repeat: no-repeat !important;transform:scale(1.1)!important}
    .player-nav-prev {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/player/prew_zps7bj2ah22.png)!important; background-size: contain !important;background-position: center center !important;background-repeat: no-repeat !important;transform:scale(1.1)!important}
    .player-nav-play {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/player/pause_zpszwpf9luo.png)!important; background-size: contain !important;background-position: center center !important;background-repeat: no-repeat !important;transform:scale(1.1)!important}
    .player-nav-play.stop {background: url(http://i1164.photobucket.com/albums/q575/Sanya4ever/player/pause_zpszwpf9luo.png)!important; background-size: contain !important;background-position: center center !important;background-repeat: no-repeat !important;transform:scale(1.1)!important}    
  .player-nav-next:hover, .player-nav-prev:hover, .player-nav-play:hover, .player-nav-play.stop:hover{transform:scale(1.2)!important;transition:all 250ms ease-in-out !important}  
    
    /* login */
  .pop_gr_t, .pop_box{background: rgba(0, 0, 0, 0.7)!important;border:none!important;padding:15px!important}     
  .pop_crn_lt,  .pop_crn_rt, .pop_gr_l, .pop_gr_r, .pop_crn_lb.grey, .pop_crn_rb.grey, .pop_gr_b.grey, .pop_crn_lb, .pop_crn_rb, .pop_gr_b{background:transparent!important}    
        
    /* radio & vk */  
  .radio_clmn {opacity:.2!important;transition:all 950ms ease-in-out !important}
  .right-banner {opacity:.04!important;transition:all 950ms ease-in-out !important}
   .radio_clmn:hover, .right-banner:hover {opacity:1!important;transition:all 950ms ease-in-out !important} 
    
    /* playlist */  
  #plpop, .other_songs .song-wrap:hover, #plpop-songs .song-wrap:hover, .playlist-wrap:hover, .playlist-wrap.renamed, .btn-green {background: rgba(0, 0, 0, 0.65)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border-radius:10px!important;transition:all 450ms ease-in-out !important}
    .playlist-wrap.renamed .playlist-rename input, #plpop .arrow{border:none!important;background:transparent!important}
  .playlists-list-item, .dws_item {border:none!important}    
     
  /* ads */
  .adv-list-top {display:none!important}}
