css:
@-moz-document url-prefix("http://www.roblox.com/games") {
body
{
    background: transparent !important;
}
.content
{
    background: transparent !important;
}
#fb-root
{
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("http://cdn.wonderfulengineering.com/wp-content/uploads/2014/07/black-wallpapers-28.jpg") !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
h1
{
    color: #FFF;
}
h2
{
    color: #FFF;
}
h3
{
    color: #FFF;
}
h4
{
    color: #FFF;
}
span
{
    color: #999;
}
p
{
    color: #FFF;
}
body
{
    color: #FFF;
}
div.col-xs-12.section.game-main-content
{
    background-color: transparent;
}
div.section.game-about-container
{
    background-color: transparent;
}
.game-stats-container .game-stat .stat-title
{
    color: #FFF
}
.game-stat-footer .game-copylocked-footnote
{
    color: #FFF
}
.rbx-tabs-horizontal .nav-tabs
{
    background-color: transparent
}
.rbx-tabs-horizontal .rbx-tab-heading
{
    color: #FFF
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus
{
    color: #FFF
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus
{
    background-color: transparent
}
.section
{
    background-color: transparent
}
.container-footer
{
    background-color: transparent;
}
.a,
a:link,
a:visited,
a:active
{
    color: #FFF;
}
.rbx-input-group-btn .rbx-input-dropdown-btn
{
    background-color: transparent;
}
.games-list-container
{
    background-color: transparent;
}
.section-row
{
    background-color: transparent;
}
.badge-row .badge-data-container p
{
    background-color: transparent;
}
.badge-row .badge-data-container p:after
{
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    height: 1px;
    width: 1px;
}
.rbx-btn-control-sm
{
    background-color: transparent;
    color: #FFF;
}
.rbx-header a .rbx-header a:link,
.rbx-header a:active,
.rbx-header a:hover,
.rbx-header a:visited,
.rbx-header a:focus
{
    color: #FFF;
}
.rbx-input-group-btn .rbx-input-dropdown-btn:hover,
.rbx-input-group-btn .rbx-input-dropdown-btn:focus
{
    background-color: #CC0000
}
.rbx-input-group-btn .rbx-input-dropdown-btn:hover,
.rbx-input-group-btn .rbx-input-dropdown-btn:focus
{
    border-color: #FFF
}
.game .game-item
{
    background-color: transparent;
}
.rbx-text-overflow
{
    background-color: transparent;
}
.rbx-text-overflow:after
{
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    height: 1px;
    width: 1px;
}
#vorlias-nav > a:nth-child(5) > span
{
    color: #393939
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading
{
    box-shadow: 0 -4px 0 0 #393939 inset
}
.scroller,
#chat_messages,
#chat_input textarea,
.tab,
.tab-container>div,
.ph-installinstructions
{
    background-color: #393939
}
.tab.active,
.tab-container>.tab-active,
.tab-container>.tab-active:hover
{
    background-color: #000
}
.scroller,
#chat_messages,
#chat_input textarea,
.tab,
.tab-container>div
{
    background-color: #393939
}
.scroller:hover,
#chat_messages:hover,
#chat_input textarea:hover,
.tab:hover,
.tab-container>div:hover
{
    background-color: #000
}
.rbx-para-overflow,
.rbx-item-card,
#horizontal-tabs,
.section,
.game-instances,
.developer-store,
.container-list
{
    background-color: transparent
}
.rbx-para-overflow:after,
.rbx-item-card .rbx-caption p:after
{
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    height: 1px;
    width: 1px;
}
.rbx-alert-info
{
    background-color: #393939
}
.rbx-btn-primary-lg
{
    background-color: transparent;
    border-color: #CCC
}
.rbx-header,
.party-window .title,
div#friend_dock_thumb_container
{
    background-color: #393939;
}
.rbx-btn-secondary-sm,
.rbx-btn-secondary-xs,
.rbx-btn-control-xs,
.rbx-item-card .rbx-caption p,
.rbx-btn-buy-xs
{
    background-color: transparent;
    color: white;
    border-color: #CCC
}
.party-window .main
{
    background-color: rgba(39, 39, 39, 0.9);
}
.rbx-btn-control-sm:hover,
.rbx-btn-control-sm:focus,
.rbx-btn-secondary-sm:hover,
.rbx-btn-secondary-sm:focus,
.rbx-btn-primary-lg:hover,
.rbx-btn-primary-lg:focus,
.rbx-btn-secondary-xs:hover,
.rbx-btn-secondary-xs:focus,
.rbx-btn-control-xs:hover,
.rbx-btn-control-xs:focus,
.rbx-btn-buy-xs:hover,
.rbx-btn-buy-xs:focus
{
    background-color: rgba(255, 255, 255, 0.16);
    color: white;
    border-color: white
}
.rbx-tabs-horizontal .nav-tabs:hover,
.rbx-tabs-horizontal .nav-tabs:focus
{
    color: white
}
.vorlias-notification,
.span,
#vorlias-nav > a:nth-child(5) > span
{
    background-color: #FFF
}
.rbx-btn-primary-lg
{
    width: 100%;
    -webkit-animation: mymove 5s once;
    animation: mymove 5s infinite;
}
@-webkit-keyframes mymove
{
    50%
{
        width: 200px;
    }
}
@keyframes mymove
{
    50%
{
        width: 200px;
    }
}
.footer-note:after
{
    visibility: visible;
    position: absolute;
    color: red;
    font-style: italic;
    font-weight: bold;
    content: "Theme created by FCEFEAR.";
}
}
