css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
body {background: url('http://media.tumblr.com/1a0567744b943929e03d09b9ae26d4e2/tumblr_inline_mrm6pxZBuG1qz4rgp.png') center right #fff repeat fixed !important;}

body, a {
cursor: url('http://img09.deviantart.net/4d01/i/2012/163/a/3/madoka_cursor_by_anime_rai-d5379s6.gif'), progress !important;
}


*{text-shadow:none !important; color:  #655E62 !important;} 


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:transparent !important;} 

#nav .nav_item.active 

	{background-color:transparent;} 

.permalink  
	{background: none !important;  
  	background: #ffe6e7 !important; 
        width: 16px !important; 
	height: 16px !important; 
  	border-bottom-left-radius: 10px !important; 
  	border-top-right-radius: 10px !important; 
  	margin-top: 3px !important; 
  	margin-right: 4px !important;} 



h1,h2,h3,h4,h5,h6 

	{url:"http://38.media.tumblr.com/4ca2ae8ce9ce4d54fdedc25b8d5dc8c7/tumblr_inline_mua4o9wWQU1qa3mpb.png" !important;} 

a:hover 

	{color:#655E62 !important;}

a, a:visited 

	{color: #655E62 !important;} 



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"])  



	{background: none !important; 

	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 11px white !important; 
	border-right: 11px solid transparent !important; 
	border-left: 11px solid transparent !important; } 


.selection_nipple:not([class="selection_nipple white"])  

	{border-bottom: solid 10px #ffe6e7 !important;}

.notification, .avatar_frame, .nipple{border:0px !important}



#dashboard_switch_blog_menu_current, 

#dashboard_switch_blog_arrow_and_divider, 

#dashboard_switch_blog_arrow_current 

	{background-image:none !important;} 

#new_post_notice_container 

	{color:#fff !important;} 

#new_post_notice_container a 

	{color:#fff !important;} 



ol#posts li.notification 

	{background-color:transparent  !important; 
	color: #000000 !important;} 


.notification_type_icon{display:none !important} 

.selected .posts, 

.selected .messages, 

.selected .following, 

.selected .followers, 

.selected .settings, 

.selected .drafts, 

.selected .queue,  

.selected .likes  

	{color: #ffbac3 !important; 

	background: #fcedee !important;} 

a.explore_more_tags, 

a.add_and_remove, 

a.customize, 

a.mass_editor 

	{background-color:transparent !important;   

	background-image:none !important; 

	border:0px !important;} 


div.gradient, 

div.dashboard_nav_item,  

a.permalink 

	{background-color:transparent !important; 

	opacity:.9;  

	background-image:none !important; 

	border:0px !important;} 

.content.is_media,  

.mask.top,  

.mask.bottom  

	{background: none !important; } 

.post.photo_post .photo,  

.post.photo_post .video_thumbnail  

	{border-radius: 16px !important;} 

.post.photo_post .photo,  



.post.photo_post .video_thumbnail  



	{-moz-box-shadow: 1px 1px 3px #000000 !important;} 

#auto_pagination_loader 


	{background: #fff !important;} 


div.return_to_top_icon{	background: none !important; 

	width: 0px !important; 

	height: 0px !important; 

	margin-top: 0px !important; 

	border-bottom: solid 30px white !important; 

	border-right: 15px solid transparent !important; 

	border-left: 15px solid transparent !important;} 

#left_column #posts .post .permalink 

	{background-color:#ffe6e7;} 


#left_column #posts .notification  

	{background:solid transparent !important; 

	color:#ffc7d0 !important; 

	border-bottom: solid transparent !important;} 

#left_column #posts .post .post_avatar 

	{-moz-border-radius: 32px !important;  

	-webkit-border-radius: 50px !important;  

	width: 64px !important; height: 64px !important;  

	margin: 0px !important;} 



#left_column #posts .post .sub_avatar_inner 

	{-moz-border-radius: 32px !important;  

	-webkit-border-radius: 50px !important;  

	width: 32px !important; height: 32px !important;  



	margin: 0px !important;} 







.new_post_label_icon{background-image:none !important;}



#new_post{background: url('http://ih0.redbubble.net/image.14242822.6214/fc,550x550,white.u2.jpg') center repeat; background-color:transparent !important;}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



    width:0;

    height:0px;

    opacity: 10;

    display: block;}



.new_post_label{



    font-size:0px !important;



    padding-top:0px !important;



    opacity: .9;}
}
