css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com"), domain("tumblr.com") {

.tab.iconic:nth-child(1) > .tab_anchor::before,

.tab.iconic:nth-child(2) > .tab_anchor::before,

.tab.iconic:nth-child(3) > .tab_anchor::before,

.tab.iconic:nth-child(4) > .tab_anchor::before,

.tab.iconic:nth-child(5) > .tab_anchor::before,

.tab.iconic:nth-child(6) > .tab_anchor::before{

	content: none!important;}



.l-header-container--refresh .tab_bar .tab_nav_account .tab_anchor i:before, .l-header-container--refresh .tab_bar .tab_nav_account .tab_anchor .icon_arrow_carrot_down {

    display: none;}



/* negative moves to the left */

.tab_bar .tab_home .tab_anchor {background-position: 5px 0px}

.tab_bar .tab_discover .tab_anchor {background-position: -47px 0px}

.tab_bar .tab_inbox .tab_anchor {background-position: -102px 0px}

.tab_bar .tab_messaging .tab_anchor {background-position: -163px 0px}

.tab_bar .tab_activity .tab_anchor {background-position: -197px 0px}

/* XKit Logo Goes Here */

.tab_bar .tab_nav_account .tab_anchor {background-position: -311px 0px}



.tab_bar .iconic .tab_anchor {

	background-image: url('http://i484.photobucket.com/albums/rr210/Shawna_Tatsu/New%20Villain%20Logo_zpswrv1mcbf.png');

	background-size: 353px 35px;

	background-repeat: no-repeat;

	cursor: pointer;

	width: 29px;}



#xkit-control a.tab_anchor, #new-xkit-control > a {

	background: url('http://i484.photobucket.com/albums/rr210/Shawna_Tatsu/New%20Villain%20Logo_zpswrv1mcbf.png') !important;

	background-position: -254px 0px !important;}



/* Notifications */

.tab_bar .tab_home .tab_notice {left: 35px;}

.tab_bar .tab_messaging .tab_notice {left: 20px;}

}
