css:
@-moz-document url-prefix("http://94.245.155.29/") {
/* 
	Overall 
*/
#all, body
{
	background-color: #fafafa !important;
}
#logo, #main, .usual
{
	width: 960px !important;
}
#logo
{
	background: /*[[primaryColor]]*/ !important;
}
#main
{
	border: 1px #dadada solid !important; 
	margin-bottom: 20px;
}
.usual div
{
	background: /*[[primaryColor]]*/ !important;
}
#torr_wrap
{
	border-top: 1px #dadada solid; 
	padding: 5px 0 0 0;
}
#footer
{
	background: none !important;
}
#footer table
{
	width: 960px !important;
}
#foo tbody
{
	background: /*[[primaryColor]]*/;
}

div[style="padding:0 5px; border-left:1px solid #ccc; border-right:1px solid #ccc"]
{
	border: 0px !important;
	padding: 0px 10px !important;
}

/*
	Tracker News
*/
.unews
{
	border: 1px #CACACA solid; 
	padding: 5px !important;
	background: #f0f0f0;
	height: auto !important;
}
.blockcontent tr[bgcolor="#f5f5f5"], td.clock
{
	background: none !important;
}

/* 
	Inputs and controls 
*/
.btn 
{
	background:/*[[primaryColor]]*/;
	border: none;
	padding: 5px 5px 3px 5px;
}
.btn:hover 
{
	background: /*[[primaryColorHover]]*/; 
}
#searchtr input[type="submit"]
{
	margin-top: 0px;
	padding: 7px;
}
.pager
{
	background: none !important;
	border:none !important;
	padding: 0px !important;
}
.pager:hover 
{
	background-color: /*[[primaryColorHover]]*/ !important;
}
.pagercurrent
{
	background: /*[[primaryColor]]*/;
	padding: 5px 10px !important;
	border: none !important;
	color: white;
}
input[value="Перейти"]
{
	padding: 5px 8px 5px 8px !important;
	border: 0px !important;
	margin-top: -4px !important;
}
#letmpg
{
	padding: 1px 3px 2px 3px !important;
}
.pager a
{
	background: #f0f0f0;
	padding: 5px 10px !important;
}
.pager a:link, .pager a:hover, .pager a:visited
{
	color: black !important;
}
#searchtr input[type="submit"], input[value="Найти"]
{
	padding: 9px 14px 8px 14px;
}
select[name="category"], select[name="active"]
{
	padding: 5px;
}
input[id="letmpg"]
{
	padding: 3px;
}


/* 
	Disable Sidebars
*/
td[width="220"]
{
	display: none;
}


/*
	LISTS
*/
table.lista td.header
{
	background-color: #F0F0F0 !important; 
	color: #333333; 
	font-weight: bold; 
	padding: 0px 0px 0px 8px; 
	text-align: left;
}
table.lista
{
	border-spacing: 0;
}
td.lista a
{
	padding: 4px 0px 4px 8px !important;
}

td[width="150"] td.blockcontent table.lista td.lista a:hover 
{
	background-color:#f5f7f9; 
	display:block
}

td[width="150"] td.blockcontent table.lista td.lista a 
{
	padding: 3px;
}
td .hide
{
	display: table-cell;
}


/*
	Torrents List 
*/
.list_dotted td 
{
	border-right: 1px solid #CACACA !important;
}
td[width="15"] 
{
	border-left: 1px solid #CACACA !important;
}
#bot_dot,.list_dotted td 
{
	border-bottom: 1px solid #CACACA;
}
#bgtorrlist2 tr.list_dotted:hover 
{
	background-color: #f5f7f9;
}
#torrbg tr:first-child
{
	font-weight: bold;
}


/*
	Torrent View
*/
#moder 
{
	padding-top: 15px;
}
#moder img
{
	display: none;
}
#torr_wrap #moder span
{
	font-size: 14px;
}
#main_text
{
	width: 630px;
	float: left;
}
#main_img
{
	max-width: 250px;
}

td.block 
{
	background:/*[[primaryColor]]*/ !important;
}

.green img, .red img, .black img
{
	height: 9px;
}
.stat1 img
{
	display: none;

}
.stat1
{
	color:black !important;
	background: #FAFAFA;
	border: 1px #F0F0F0 solid;
}

.stat1 a
{
	color: blue !important;
}
#dload
{
	padding: 0 !important;
	margin: 6px 3px !important;
}
#dloadl
{
	background: #FAFAFA !important;
	border: 1px #F0F0F0 solid;
	padding: 5px 0px;
}
#stats
{
	margin: 0px !important;
}
#other
{
	background: #FAFAFA !important;
	border: 1px #F0F0F0 solid !important;
	padding: 10px;
	margin: 3px !important;
}
#other a[name="expand"]
{
	color: blue;
	font-weight: bold;
	font-size: 14px;
}
.headercc
{
	background: none !important;
	color: black !important;
	padding: 5px 0px !important;
	height: auto !important;
}
#prev
{
	margin: 25px 10px !important;
}
#prev div
{
	margin-right: 5px;
	padding: 0;
}
#com_head td
{
	background: #FAFAFA;
	border: 1px #F0F0F0 solid;
	padding: 0px 10px 0px 10px !important;
}

#com_head td a
{
	font-weight: bold;
}

.listc
{
	border: none !important;
	padding: 10px !important;
}


/*
	Users List
*/
select[name="level"]
{
	padding: 1px;
}
input[value="Искать"]
{
	padding: 5px 10px 4px 10px;
}


/* 
	Disable Advertisement 
*/
a[href="/doc/reklama.html"]
{
	display: none !important;
}

.mega-banner,#minibaner, iframe, object, td[width="50%"], #ad600x100
{
	display:none !important;
}
}
