css:
@-moz-document domain("emuparadise.me"){
/* Remove ads */
div[id*="site-skin"],
#kona_0_wrapper,
#MarketGidComposite14111,
iframe,
ad_300_250,
ad_filler,
#kona_6_wrapper,
div[id*="kona"] {
    display: none !important }

/* Remove tons of stuff */
.menu,
.box[style="background-color:#0E2631;padding:5px"],
.col-9 .mobile-hide,
.box .mobile-hide,
.handle,
.col_3  > :nth-child(2),
.col_3  > :nth-child(4),
.col_3  > :nth-child(5),
a.intro-to-emulation,
div[itemtype="http://schema.org/SoftwareApplication"],
ol#quick-nav,
div[itemtype="http://schema.org/SoftwareApplication"] + h2,
div[itemprop="aggregateRating"],
div[itemprop="aggregateRating"] + form,
a.prettybutton,
#user-uploaded-images +h2,
em,
hr,
#tag-div,
#similar-games,
#Emu_Recommendation,
#Emu_Recommendation + hr + form,
#Emulators,
#Discuss,
#User_Media,
#user-uploaded-images,
br[clear="left"],
br[clear="all"],
#Support_Emuparadise,
#PlayOnline,
#rating-form,
#bonus,
.help,
.tip,
h2[style="color:#a5d414;"],
#search-submit-button,
#homepage-video,
#nominate,
#emuvote-message,
.video-container,
div[align="center"] > h2,
td a[href*="-all"] b,
h2[style="clear:both;"] {
    display: none !important }

/* Remove some optional stuff */
.mobile-hide,
#Game_Descriptions,
#game-descriptions,
p {
    display: none !important }

.col_9 {    
    width: auto !important;
    float: none !important; }

.systems-display {
    margin-right: 10px;
    margin-left: 10px;
    width: 30% !important}

/* Smaller Top Bar */
body {
    background-size: 1px 75px;}

#logo-row {
    height: 80px;}

#eplogo img {
    height: 50%;
    width: 50%; }

.search {
    top: 1px;
    left: auto !important;
    position: relative !important;
    float: right;}

#topsearchform {
    display: inline !important;}
}
