    }
.thumbnail {
    background: transparent none repeat scroll 0% 0%;
    border: 2px solid #337580 !important;
    border-radius: 9px;
    display: block;
    float: left;
    font-size: 0px;
    height: 70px !important;
    width: 70px !important;
    line-height: 100%;
    margin: 10px 4px 0px 0px;
    max-width: 72px;
    overflow: hidden;
