    border-bottom: 2px solid #394870 !important;
    border-right: none !important;
    border-radius: 25px;
    color: #337580 !important;
    }
.md blockquote p::before { content: open-quote; }
.md blockquote p::after { content: close-quote; }
.md blockquote{
    border: none !important;
    border-left: 2px solid #394870  !important;
    border-radius: 25px;
    color: #337580 !important;
    padding-top: 3px !important;
    }
    
a, a:visited, .link a.author, .link a.author:visited {
