css:
@-moz-document url-prefix("https://irccloud.com/"), url-prefix("https://www.irccloud.com/"), url-prefix("https://alpha.irccloud.com/") {
	


	div.viewport, .connectionstatuscell {
		background: #000 !important;
	}

	body, body.app, div#sidebar {
		background: #000 !important;
	}




	div#settingsContainer, div.mainOverlayContent, div.accountContainer, div.accountContainer h2, div.contextMenu, div.bufferContextMenu {
  		font-family: "Consolas" !important;
		font-size: 12px !important;
	}

	#sidebar {
  		font-family: "Consolas" !important;
		font-size: 13px !important;
	}

	#footer {
  		font-family: "Consolas" !important;
  		font-size: 12px !important;
  		color: #aaa !important;
	}

	#footer a {
  		color: #FAAC58 !important;
	}

	div.viewport, .mono, input, textarea, button, select, div.shim, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user, td.statuscell div.status h2.bufferHeading span.label, span.topic, ul.memberList, td.nickcell a.nick, td.inputcell textarea, div.log div.messageRow, div.log div.me a.user, div.log div.me a.server, h1#title span.logotype span.beta, .form .input, div.log div.monospace span.message, #channelContextMenu ul.modeList {
		font-family: "Consolas" !important;
  		font-size: 13px !important;
	}
	
	body.app, div#channelContextMenu ul.modeList li {
		font-family: "Consolas" !important;
		font-size: 12px !important;
	}

	div.log div.hover span.date {
    color: #0040FF !important; /* nickname üzerine gelince o kişinin mesajlarını saatle birlikte belirten renk ayarı */
  }
  div#timeContainer, div.log span.date, .gist .gist-meta, div.log div.gistwrap h1.gistwraptitle, body:not(.nick-colors) a.author,
  div.log, div.paste h1 {
    color: #424242 !important; /* saatin yazı rengi */
  }
  div.log div.chat, div.log div.error, div.log div.groupedLines, div.log div.me, div.log div.notice, div.log div.status,
  div.log div.table, div.log div.warning, div.buffermain, button a, button span, div.dateWrapper table, div.log div.dateChange p,
  #channelContextMenu.active.mod ul.actions li a.topic, #memberContextMenu ul.actions li a,
  td.statuscell div.status h2.bufferHeading span.label, .gist .line-pre, #memberContextMenu div.userContext, .contextMenu a,
  .gist, .gist .highlight, div.paste h1 span.actions a, div.log div.groupedJoinPart span.mode, div.log span.prefix,
  #channelContextMenu.topicControl ul.actions li a.topic, .bufferContextMenu ul.actions li a, #uploadContextMenu ul.actions li a:hover {
    color: #BDBDBD !important; /* mesajların rengi */
  }
  #buffers .active a.buffer, #footer a, #headercell a, #sidebar #limits a, h1#title a, div#searchResults div.searchResultDate a,
  a.gist, a.link, a.reconnect, span.reconnect, #buffers a.buffer, #buffers p.archiveToggle a, #buffers p.join a, .gist a,
  div.log div.chat a:not(.c0):not(.c1):not(.c2):not(.c3):not(.c4):not(.c5):not(.c6):not(.c7):not(.c8):not(.c9):not(.c10):not(.c11):not(.c12):not(.c13),
  div.log div.notice a {
    color: #0040FF !important;
  }
  .bufferHead {
    padding: 0px !important;
  }
  div.log div.row.highlight {
    background: #0431B4 !important; /* mention olunan mesajın arka plan rengi */
  }
  div.log div.self, .connectionStatus.disconnect a, .connectionStatus.edit a, .connectionStatus.reconnect a, .connectionStatus.upgrade a,
  #pasteSidebar, .ui-autocomplete .ui-state-focus, .ui-autocomplete .ui-state-focus span.host, div.log div.groupedLines {
    background: #000 !important; /* kendi mesajlarımın arka plan rengi */
  }
  div.buffermain, .gist .line-numbers {
    background: #000 !important; /* mesaj yazılan alanın dış çerçeve rengi */
  }
  input, textarea, td.nickcell, td.inputcell {
    background: #000 !important; /* mesaj yazılan alanın rengi */
  }
  /* hide stuff */
  #sidebar #limits, h1#title span.logo, h1#title span.logotype, #loggedInAs {
    display: none !important;
  }
}
