css:
@-moz-document domain(mail.yandex.ru), domain(mail.yandex-team.ru) {
	/* full width: */
    .mail-App-Cover
    {
        background: none !important;
    }
    .mail-Layout
    {
        width: 100% !important;
        max-width: 100%;
    }
    .mail-App-Header
    {
        padding: 0 20px;
    }

	.mail-Layout-Aside-Inner
	{
        padding-right: 2px;
        margin-right: -2px;
    }

    /* thin scrollbars */
    ::-webkit-scrollbar
    {
        width: 2px !important;
        height:2px !important; 
        -webkit-box-shadow: none !important;
        box-shadow: none !important;
    }
    ::-webkit-scrollbar-thumb
    {
        border-width: 0 !important;
        border-radius: 0;
    }
	.ui-resizable-handle.ui-resizable-e
	{
    	background-color: rgba(0,0,0,.05);
    }
	.mail-Page-Body .ui-resizable-e
	{
    	width: 2px;
    }
	.mail-Layout-Panes .ui-resizable-e
	{
    	opacity: 0;
    }

    /* list items */
    .theme-colorful .mail-NestedList-Item:hover,
    .mail-MessageSnippet:not(.mail-NestedList-Item_current):hover .mail-MessageSnippet-Content
    {
        background-color: #fffdeb !important;
    }
    .theme-colorful .mail-NestedList-Item_current.mail-NestedList-Item_current,
    .mail-Layout_3pane .mail-MessageSnippet-Wrap .is-focused > .mail-MessageSnippet-Wrapper > .mail-MessageSnippet .mail-MessageSnippet-Content
    {
        background-color: #fff8be !important;

    }
    .mail-MessageSnippet-Wrap
    {
        transition: none;
    }

    /* sidebar */
    .mail-Layout-Aside
    {
        background-color: #FFF !important;
    }
    .mail-Layout-Aside ::-webkit-scrollbar-thumb,
    .mail-Layout-Aside::-webkit-scrollbar-thumb
    {
        box-shadow: -2px 0 rgba(0,0,0,0.2) !important;
    }

    /* item in sidebar */
    .mail-NestedList-Item
    {
        padding-right: 10px;
        color: #555;
    }
    .mail-NestedList-Item_current.mail-NestedList-Item_empty
    {
        color: #000;
    }
    .mail-NestedList-Item_unread .mail-NestedList-Item-Name
    {
        font-weight: 100;
    }

    .mail-NestedList-Item .mail-NestedList-Item-Info-Extras,
	.mail-NestedList-Item_current .mail-NestedList-Item-Info-Extras
	{
        display: none;
        color: #ddd;
    }
	.mail-NestedList-Item:not(.mail-NestedList-Item_current) .mail-NestedList-Item-Info-Extras
    {
		display: inline-flex;
    }

	/* turn off adv */
	.ns-view-banner, b-banner
	{
   		display: none;
	}
}
