css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.iwantim.com") {
html, body {
background: #000;
color:white;
}
.panel expanded{
background: #000;
color:white;
}
.panel {
background: #000;
color:white;
}
._message{
background: #000;
color:white;
}
#desktopTitlebarWrapper {
    position: relative;
    height: 62px;
    overflow: hidden;
    background: #000;
}
.column {
    position: relative;
    float: left;
    overflow: hidden;
    background: #000;
}
.groupContent{
   background: #000;
   color:white !important;
}
    .groupHeader{
        background: #000;
   color:white !important;
    }
.BuddyList{
    background: #000;
   color:white !important;
}
.mocha{
    background: #000 !important;
}
    mochaTitlebar{
    background-color: #000 !important;
        border-color: #222;
    border-width: 1px;
}
.mochaTitlebar.replaced{
    background: #000 !important;
    border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
    
}
.mochaContentBorder{
    background: #000 !important;
    border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
}
    .bottomPanel {
        background: #000 !important;
        color:white;
    }
    .mochaContentWrapper {
    font-size: 12px;
    overflow: auto;
        border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
    background: #000 none repeat scroll 0% 0%;
}
    .mochaOverlay {
    position: absolute;
    top: 0px;
    left: 0px;
        background: #000 !important;
    border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
}
    .conversation_window{
        background: #000 !important;
    border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
        color:#444 !important;
    }
.conversation_textarea{
    background: #000 !important;
    border-color: #222 !important;
    border-width: 1px !important;
    border-style:solid;
        color:#444 !important;
}
.btn, input.btn[type="submit"], input.btn[type="button"] {
    background: #000;
    font: 11px/14px "Lucida Grande",sans-serif;
    width: auto;
    overflow: visible;
    border-radius: 4px;
    color: #444;
    text-shadow: 1px 1px 0px #FFF;
    border-color: #DDD #DDD #CCC;
    border-style: solid;
    border-width: 1px;
    margin: 0px;
    padding: 4px 10px 5px;
}
.panel-headerContent{
background: #444;
}
#buddy_list_header #user_div {
    background: none;
    display: block;
    clear: both;
    height: 65px;
    overflow: visible;
    margin-bottom: 10px;
    padding: 10px 10px 0px;
}
#dock {
    position: relative;
    bottom: 0px;
    left: 0px;
    background: #000 none repeat scroll 0% 0%;
    min-height: 27px;
    height: auto;
    padding: 3px 16px 0px 6px;
}
.columnHandle {
    min-height: 10px;
    float: left;
    width: 4px;
    overflow: hidden;
    background: #111;
    border-width: 0px 1px;
    border-style: none solid;
    border-color: -moz-use-text-color #909090;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
}
}
