css:
@-moz-document domain("slickdeals.net") {
body { background: #D0DDE5; }
.avatarBox { display: none; }
.fpGridBox .fpItem .itemBottomRow a.comments .count,
.fpGridBox .fpItem .itemBottomRow .likes .count  { color: #555 !important; }
#fpMainContent .gridCategory .grid .fpItem .itemImageAndName .itemTitle { font-weight: bold; }
#detailsTop .textDescription,
.editorsNotes,
.post .postTextContent,
.forumText { font-size: 1.15em; line-height: 22px; letter-spacing: -0.1px; font-weight: 600 }
#sideColumn .sideGridBox .subBoxContent a.split { color: #333; }
#commentsBox { background: #F7F5F1; }
.box,
#commentsBox { background: #F1F5F7; border: 1px solid #9EB4C2; }
.post .postTextTop .postDateTime { color: #777; font-size: 13px; }
#originalPost { background: white; } 
.quotebox_bg .quotebox_content { background: #DFE9EF; font-size: 0.9em; color: #000 }
#dealStats { background: white; }
#breadcrumbs>a { color: #333; }
#titleArea #titleInfoRow .titleText { font-size: 12px; }


#fpMainContent .gridCategory .fpGridBox { border: 1px solid #9EB4C2; }
.adminIpRow { background: #fff; }
#commentsBox .adminIpRow { background: none;  position: absolute; left: 154px; top: -3px;  }
#aboutAuthorBox #statisticsAndSubmissions { background-color: #fff; }
#commentsBox .adminPosterNotes { margin-left: 76px; color: #555; }
#commentsBox .adminIpRow>span, .adminIpRow>a,
#commentsBox .adminPosterNotes .adminUserNotes>a, .adminPosterNotes .adminWarnLevel>a { color: #555; }
#commentsBox .post .postTextTop .postDateTime { color: #333; font-weight: 600; }
#commentsBox .post .postTextBottom { margin-top: 21px; }
#commentsBox .post .postTextTop { margin-bottom: 15px; }
#commentsBox .adminPostRow { margin-bottom: 0; }
#commentsBox .post { border-bottom: 7px solid #FFFFFF; }
.post .postUsername a { font-size: 1.2em; }
.box .boxHeader { background: #fff; border-bottom: 1px solid #ddd; }
}
